style:
body {
    display: flex;
    justify-content: center;
    align-items: center;
    height: 100vh;
    margin: 0;
    background: #f0f0f0;
}
.dots {
    display: flex;
}
.dot {
    width: 10px;
    height: 10px;
    background: #3498db;
    border-radius: 50%;
    margin: 0 5px;
    animation: bounce 1s infinite ease-in-out;
}
.dot:nth-child(2) {
    animation-delay: 0.1s;
}
.dot:nth-child(3) {
    animation-delay: 0.2s;
}
@keyframes bounce {
    0%, 100% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-15px);
    }
}
