style:
:root {
	--bg: #f8f9fa;
	--text: #212529;
	--primary: #4a90e2;
	--secondary: #e94d4d;
	--loader-speed: 1.2s;
}

[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #e8e8e8;
	--primary: #6c5ce7;
	--secondary: #ff7675;
}

body {
	margin: 0;
	padding: 2rem;
	min-height: 100vh;
	background: var(--bg);
	color: var(--text);
	transition: all 0.3s ease;
	display: flex;
	flex-direction: column;
	align-items: center;
	font-family: "Segoe UI", system-ui, sans-serif;
}

.progress-loader {
	width: 200px;
	height: 4px;
	background: rgba(0, 0, 0, 0.1);
	border-radius: 2px;
	overflow: hidden;
}

.progress-loader::after {
	content: "";
	display: block;
	width: 40%;
	height: 100%;
	background: linear-gradient(90deg, var(--primary) 0%, var(--secondary) 100%);
	animation: progress calc(var(--loader-speed) * 2) ease-in-out infinite;
}

@keyframes progress {
	0% {
		transform: translateX(-100%);
	}
	100% {
		transform: translateX(250%);
	}
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	padding: 0.75rem 1.25rem;
	background: var(--primary);
	color: var(--bg);
	border: none;
	border-radius: 8px;
	cursor: pointer;
	font-weight: 600;
	transition: transform 0.2s;
}

.theme-toggle:hover {
	transform: scale(1.05);
}

script:
function toggleTheme() {
	const body = document.body;
	const currentTheme = body.getAttribute("data-theme") || "light";
	const newTheme = currentTheme === "dark" ? "light" : "dark";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("loader-theme", newTheme);
}

// Load saved theme
const savedTheme = localStorage.getItem("loader-theme");
if (savedTheme) {
	document.body.setAttribute("data-theme", savedTheme);
}
