style:
/* Scrollbar Styles */
::-webkit-scrollbar {
	width: 8px;
}
::-webkit-scrollbar-track {
	background-color: #aaa;
}
::-webkit-scrollbar-thumb {
	background-color: #335ddc;
	border-radius: 8px;
}

/* Base Styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Open Sans", -apple-system, BlinkMacSystemFont, "Segoe UI", "Oxygen", "Ubuntu", "Cantarell",
		"Fira Sans", "Droid Sans", "Helvetica Neue";
}

body {
	background: #d9edff;
	transition: all 0.3s ease;
}

.dark-theme {
	background: #1a1a1a;
	color: #fff;
}

.dark-theme .login-box {
	background: #2d2d2d;
	box-shadow: 0 0 40px rgba(255, 255, 255, 0.1);
}

.login-page {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
}

.login-box {
	display: flex;
	justify-content: space-between;
	align-items: center;
	flex-direction: row-reverse;
	max-width: 1000px;
	background-color: white;
	box-shadow: 0 0 40px rgba(0, 0, 0, 0.16);
	overflow: hidden;
	margin: 0 auto;
	border-radius: 12px;
	transition: all 0.3s ease;
}

.form-container {
	flex: 1 0 100%;
	max-width: 480px;
	width: 100%;
	padding: 60px;
}

.form-container p {
	margin: 0 0 18px;
}

.form-title {
	color: #333333;
	font-size: 42px;
	font-weight: bold;
	margin-bottom: 10px;
}

.dark-theme .form-title {
	color: #fff;
}

.form-group {
	margin-bottom: 1.5rem;
	position: relative;
}

.input-icon {
	position: absolute;
	left: 15px;
	top: 50%;
	transform: translateY(-50%);
	color: #888;
}

input {
	width: 100%;
	padding: 12px 12px 12px 40px;
	border: 1px solid #ddd;
	border-radius: 6px;
	font-size: 1rem;
	transition: all 0.3s ease;
}

.dark-theme input {
	background: #333;
	border-color: #444;
	color: #fff;
}

.btn {
	width: 100%;
	padding: 12px;
	background: #335ddc;
	color: white;
	border: none;
	border-radius: 6px;
	cursor: pointer;
	font-size: 1rem;
	transition: background 0.3s ease;
}

.btn:hover {
	background: #2a4db3;
}

.toggle-form {
	text-align: center;
	margin-top: 1rem;
}

.toggle-form a {
	color: #335ddc;
	text-decoration: none;
	font-weight: bold;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	cursor: pointer;
	font-size: 1.5rem;
	color: #333;
	background: none;
	border: none;
}

.dark-theme .theme-toggle {
	color: #fff;
}

.hidden {
	display: none;
}

.checkbox-group {
	margin: 1rem 0;
	display: flex;
	align-items: center;
}

.checkbox-group input {
	width: auto;
	margin-right: 10px;
}

/* New Styles for Google Login and Signup */
.social-login {
	margin: 20px 0;
	text-align: center;
}

.google-btn {
	background: #4285f4;
	color: white;
	padding: 12px;
	border-radius: 6px;
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 10px;
	cursor: pointer;
	transition: background 0.3s ease;
	border: none;
	width: 100%;
}

.google-btn:hover {
	background: #357abd;
}

.separator {
	margin: 20px 0;
	position: relative;
	text-align: center;
	color: #666;
}

.dark-theme .separator {
	color: #aaa;
}

.dark-theme .separator::before,
.dark-theme .separator::after {
	background: #555;
}

.phone-input {
	display: flex;
	gap: 10px;
}

.country-code {
	width: 80px;
	padding-left: 40px !important;
}

@media screen and (max-width: 1023px) {
	.login-box {
		flex-direction: column;
		box-shadow: none;
		margin: 20px;
	}
	.illustration-wrapper {
		max-width: 100%;
		min-height: 300px;
	}
	.form-container {
		max-width: 100%;
		padding: 40px;
	}
}

script:
// Theme Toggle
function toggleTheme() {
	const body = document.body;
	const themeIcon = document.querySelector(".theme-toggle i");
	body.classList.toggle("dark-theme");

	if (body.classList.contains("dark-theme")) {
		themeIcon.classList.replace("fa-moon", "fa-sun");
		localStorage.setItem("theme", "dark");
	} else {
		themeIcon.classList.replace("fa-sun", "fa-moon");
		localStorage.setItem("theme", "light");
	}
}

// Check saved theme
window.onload = function () {
	const savedTheme = localStorage.getItem("theme");
	const themeIcon = document.querySelector(".theme-toggle i");
	if (savedTheme === "dark") {
		document.body.classList.add("dark-theme");
		themeIcon.classList.replace("fa-moon", "fa-sun");
	}
};

// Form Toggle
function toggleForms() {
	const loginForm = document.getElementById("loginForm");
	const signupForm = document.getElementById("signupForm");
	loginForm.classList.toggle("hidden");
	signupForm.classList.toggle("hidden");
}

// Form Handlers
function handleLogin(event) {
	event.preventDefault();
	const formData = new FormData(event.target);
	const data = Object.fromEntries(formData);
	console.log("Login Data:", data);
}

function handleSignup(event) {
	event.preventDefault();
	const formData = new FormData(event.target);
	const data = Object.fromEntries(formData);
	console.log("Signup Data:", data);

	// Add basic password match validation
	const password = event.target.querySelector('input[type="password"]');
	const confirmPassword = event.target.querySelectorAll('input[type="password"]')[1];

	if (password.value !== confirmPassword.value) {
		alert("Passwords do not match!");
		return;
	}
}

