style:
:root {
	--light-bg: #f0f2f5;
	--light-text: #2c3e50;
	--light-primary: #3498db;
	--light-card: rgba(255, 255, 255, 0.9);

	--dark-bg: #0f172a;
	--dark-text: #f8fafc;
	--dark-primary: #818cf8;
	--dark-card: rgba(30, 41, 59, 0.9);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	font-family: "Inter", sans-serif;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--light-bg);
	color: var(--light-text);
	position: relative;
	overflow: hidden;
}

body[data-theme="dark"] {
	background: var(--dark-bg);
	color: var(--dark-text);
}

.theme-toggle {
	position: fixed;
	top: 2rem;
	right: 2rem;
	background: var(--light-card);
	width: 3rem;
	height: 3rem;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
	border: none;
	color: inherit;
	z-index: 100;
}

body[data-theme="dark"] .theme-toggle {
	background: var(--dark-card);
}

.login-container {
	background: var(--light-card);
	backdrop-filter: blur(10px);
	padding: 2.5rem;
	border-radius: 1.5rem;
	width: 400px;
	box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
	position: relative;
	z-index: 10;
}

body[data-theme="dark"] .login-container {
	background: var(--dark-card);
	box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.login-header {
	text-align: center;
	margin-bottom: 2rem;
}

.login-header h1 {
	font-size: 1.8rem;
	margin-bottom: 0.5rem;
}

.input-group {
	margin-bottom: 1.5rem;
	position: relative;
}

.input-group input {
	width: 100%;
	padding: 1rem 1rem 1rem 2.5rem;
	border: 2px solid transparent;
	border-radius: 0.8rem;
	background: rgba(255, 255, 255, 0.1);
	color: inherit;
	font-size: 1rem;
}

.input-group input:focus {
	outline: none;
	border-color: var(--light-primary);
	background: rgba(255, 255, 255, 0.2);
}

body[data-theme="dark"] .input-group input:focus {
	border-color: var(--dark-primary);
}

.input-group i {
	position: absolute;
	left: 1rem;
	top: 50%;
	transform: translateY(-50%);
	color: var(--light-primary);
}

body[data-theme="dark"] .input-group i {
	color: var(--dark-primary);
}

#password-input-group i:last-child {
	left: auto;
	right: 1rem;
}

.password-toggle {
	position: absolute;
	right: 1rem;
	top: 50%;
	transform: translateY(-50%);
	cursor: pointer;
	color: inherit;
}

button[type="submit"] {
	width: 100%;
	padding: 1rem;
	background: var(--light-primary);
	color: white;
	border: none;
	border-radius: 0.8rem;
	font-size: 1rem;
	font-weight: 600;
	cursor: pointer;
	transition: transform 0.2s;
}

body[data-theme="dark"] button[type="submit"] {
	background: var(--dark-primary);
}

button[type="submit"]:hover {
	transform: translateY(-2px);
}

.social-login {
	margin-top: 2rem;
	display: flex;
	justify-content: center;
	gap: 1rem;
}

.social-icon {
	width: 40px;
	height: 40px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	background: rgba(255, 255, 255, 0.1);
	cursor: pointer;
	color: inherit;
}

.social-icon:hover {
	background: rgba(255, 255, 255, 0.2);
}

@keyframes float {
	0%,
	100% {
		transform: translate(0, 0);
	}
	25% {
		transform: translate(10px, 10px);
	}
	50% {
		transform: translate(-10px, 15px);
	}
	75% {
		transform: translate(15px, -5px);
	}
}

@media (max-width: 480px) {
	.login-container {
		width: 90%;
		padding: 1.5rem;
	}
}

script:
// Theme toggle
const themeToggle = document.getElementById("themeToggle");
const body = document.body;

// Load saved theme
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme);
themeToggle.innerHTML = savedTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';

themeToggle.addEventListener("click", () => {
	const currentTheme = body.getAttribute("data-theme");
	const newTheme = currentTheme === "light" ? "dark" : "light";
	body.setAttribute("data-theme", newTheme);
	localStorage.setItem("theme", newTheme);
	themeToggle.innerHTML = newTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
});

// Password toggle
function togglePassword(icon) {
	const input = icon.previousElementSibling;
	input.type = input.type === "password" ? "text" : "password";
	icon.classList.toggle("fa-eye-slash");
	icon.classList.toggle("fa-eye");
}
