style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

:root {
	--body-bg: rgb(27, 27, 27);
	--nav-bg: aliceblue;
	--text-color: var(--body-bg);
	--indicator-color: #7c3aed;
	--indicator-border: var(--body-bg);
	--clip-size: 1.2rem;
}

[data-theme="dark"] {
	--body-bg: aliceblue;
	--nav-bg: rgb(27, 27, 27);
	--text-color: aliceblue;
	--indicator-border: var(--body-bg);
}

body {
	display: flex;
	align-items: center;
	justify-content: center;
	min-height: 100dvh;
	font-family: Arial, Helvetica, sans-serif;
	background-color: var(--body-bg);
	transition: background-color 0.3s;
}

nav {
	background-color: var(--nav-bg);
	width: 578px;
	height: 4.8rem;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 0.8rem 0.8rem 0 0;
	transition: background-color 0.3s;
}

@media screen and (max-width: 768px) {
	nav {
		max-width: 100%;
		width: 100%;
	}
}

ul {
	width: 100%;
	list-style: none;
	display: flex;
	justify-content: space-between;
	padding: 20px;
	position: relative;
}

ul li {
	position: relative;
	width: 4.8rem;
	height: 4.8rem;
	z-index: 1;
}

ul li a {
	position: relative;
	display: flex;
	align-items: center;
	justify-content: center;
	flex-direction: column;
	width: 100%;
	text-decoration: none;
	color: var(--text-color);
	cursor: pointer;
	transition: color 0.3s;
}

.icon {
	position: relative;
	display: block;
	line-height: 4.8rem;
	font-size: 1.6rem;
	text-align: center;
	transition: 0.5s ease;
	color: var(--text-color);
}

li.active .icon {
	transform: translateY(-2.4rem);
	color: aliceblue;
}

span.text {
	position: absolute;
	transition: 0.5s ease;
	opacity: 0;
	transform: translateY(1.2rem);
	font-size: 0.75rem;
	font-weight: 600;
	color: inherit;
}

li.active span.text {
	opacity: 1;
}

.indicator {
	position: absolute;
	width: 4.8rem;
	height: 4.8rem;
	background-color: var(--indicator-color);
	border-radius: 50%;
	border: 0.4rem solid var(--indicator-border);
	top: 50%;
	left: 50%;
	transform: translate(-50%, -100%);
	transition: 0.5s ease;
}

.indicator::before {
	content: " ";
	position: absolute;
	width: var(--clip-size);
	height: var(--clip-size);
	top: 50%;
	left: -1.5rem;
	border-top-right-radius: 0.8rem;
	box-shadow: 2px -9px 0px 0px var(--indicator-border);
}

.indicator::after {
	content: " ";
	position: absolute;
	width: var(--clip-size);
	height: var(--clip-size);
	top: 50%;
	right: -1.5rem;
	border-top-left-radius: 0.8rem;
	box-shadow: -2px -9px 0px 0px var(--indicator-border);
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: none;
	border: none;
	cursor: pointer;
	font-size: 1.5rem;
	color: var(--nav-bg);
	transition: color 0.3s;
}

script:
const tabLinks = document.querySelectorAll(".tab-link");
const indicator = document.querySelector(".indicator");
const themeToggle = document.getElementById("themeToggle");
const themeIcon = themeToggle.querySelector("i");

themeToggle.addEventListener("click", () => {
	const isDark = document.body.getAttribute("data-theme") === "dark";
	document.body.setAttribute("data-theme", isDark ? "light" : "dark");
	themeIcon.className = isDark ? "fas fa-moon" : "fas fa-sun";
});

tabLinks.forEach((link, index) => {
	link.addEventListener("click", () => {
		tabLinks.forEach((e) => e.classList.remove("active"));
		link.classList.add("active");
		updateIndicatorPosition();
	});
});

function updateIndicatorPosition() {
	const activeTab = document.querySelector(".tab-link.active");
	if (!activeTab) return;

	const parentRect = activeTab.parentElement.getBoundingClientRect();
	const tabRect = activeTab.getBoundingClientRect();
	const tabCenter = tabRect.left - parentRect.left + tabRect.width / 2;

	indicator.style.left = `${tabCenter}px`;
}

window.addEventListener("resize", updateIndicatorPosition);
window.addEventListener("DOMContentLoaded", updateIndicatorPosition);
