style:
:root {
	--primary-color: #6366f1;
	--bg-primary: #ffffff;
	--bg-secondary: #f8fafc;
	--text-primary: #1e293b;
	--text-secondary: #64748b;
	--border-color: #e2e8f0;
	--hover-bg: #f1f5f9;
	--active-bg: #e0e7ff;
	--transition-speed: 0.3s;
	--sidebar-width: 240px;
}

.dark-theme {
	--primary-color: #818cf8;
	--bg-primary: #1e293b;
	--bg-secondary: #0f172a;
	--text-primary: #f8fafc;
	--text-secondary: #94a3b8;
	--border-color: #334155;
	--hover-bg: #1f2937;
	--active-bg: #3730a3;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Inter", sans-serif;
	background: var(--bg-secondary);
	min-height: 100vh;
	transition: background-color var(--transition-speed);
}

.sidebar {
	width: var(--sidebar-width);
	height: 100vh;
	background: var(--bg-primary);
	position: fixed;
	top: 0;
	left: 0;
	display: flex;
	flex-direction: column;
	transition: width var(--transition-speed);
	box-shadow: 2px 0 12px rgba(0, 0, 0, 0.05);
}

.sidebar-header {
	padding: 1.5rem;
	display: flex;
	align-items: center;
	gap: 1rem;
	border-bottom: 1px solid var(--border-color);
}

.logo {
	font-size: 1.25rem;
	font-weight: 600;
	color: var(--primary-color);
	white-space: nowrap;
	overflow: hidden;
	transition: opacity var(--transition-speed);
}

.toggle-btn {
	background: none;
	border: none;
	color: var(--text-primary);
	cursor: pointer;
	padding: 0.5rem;
	border-radius: 8px;
	transition: background-color var(--transition-speed);
}

.toggle-btn i {
	font-size: 1.2rem;
}

.toggle-btn:hover {
	background: var(--hover-bg);
}

.sidebar-nav {
	flex-grow: 1;
	padding: 1rem;
}

.nav-group {
	margin-bottom: 1.5rem;
}

.nav-title {
	color: var(--text-secondary);
	font-size: 0.875rem;
	text-transform: uppercase;
	margin-bottom: 0.5rem;
	padding-left: 0.5rem;
	transition: opacity var(--transition-speed);
}

.nav-item {
	list-style: none;
	margin-bottom: 0.25rem;
}

.nav-link {
	display: flex;
	align-items: center;
	gap: 1rem;
	padding: 0.75rem;
	color: var(--text-primary);
	text-decoration: none;
	border-radius: 8px;
	transition: background-color var(--transition-speed), transform var(--transition-speed);
	position: relative;
	cursor: pointer;
}

.nav-link:hover {
	background: var(--hover-bg);
	transform: translateX(4px);
}

.nav-link.active {
	background: var(--active-bg);
	color: var(--primary-color);
	font-weight: 500;
}

.nav-icon {
	font-size: 1.1rem;
	min-width: 24px;
	display: flex;
	justify-content: center;
}

.nav-text {
	white-space: nowrap;
	overflow: hidden;
	transition: opacity var(--transition-speed);
}

.tooltip {
	position: absolute;
	left: calc(100% + 15px);
	background: var(--bg-primary);
	color: var(--text-primary);
	padding: 6px 12px;
	border-radius: 6px;
	font-size: 0.875rem;
	opacity: 0;
	visibility: hidden;
	transition: opacity var(--transition-speed);
	pointer-events: none;
	white-space: nowrap;
	box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

.tooltip::before {
	content: "";
	position: absolute;
	right: 100%;
	top: 50%;
	transform: translateY(-50%);
	border-width: 5px;
	border-style: solid;
	border-color: transparent var(--bg-primary) transparent transparent;
}

/* Collapsed state */
.sidebar.collapsed {
	width: 80px;
}

.sidebar.collapsed .logo,
.sidebar.collapsed .nav-text,
.sidebar.collapsed .nav-title {
	opacity: 0;
}

.sidebar.collapsed .nav-link:hover .tooltip {
	opacity: 1;
	visibility: visible;
}

.theme-switcher {
	padding: 1rem;
	border-top: 1px solid var(--border-color);
	display: flex;
	justify-content: center;
}

.theme-toggle {
	display: flex;
	align-items: center;
	gap: 0.75rem;
	padding: 0.75rem 1rem;
	border-radius: 8px;
	background: var(--bg-secondary);
	border: none;
	cursor: pointer;
	color: var(--text-primary);
	transition: background-color var(--transition-speed);
	width: 100%;
}

.theme-toggle:hover {
	background: var(--hover-bg);
}

.toggle-icon {
	font-size: 1.1rem;
	color: var(--primary-color);
}

script:
const sidebar = document.querySelector(".sidebar");
function toggleSidebar() {
	const toggleBtn = document.querySelector(".toggle-btn i");

	sidebar.classList.toggle("collapsed");
	toggleBtn.classList.toggle("fa-bars");
	toggleBtn.classList.toggle("fa-times");
}

const navLinks = sidebar.querySelectorAll(".nav-link");

function toggleNavActive(element) {
	navLinks.forEach((item) => item.classList.remove("active"));
	element.classList.add("active");
}

function toggleTheme() {
	document.body.classList.toggle("dark-theme");
	const themeIcon = document.querySelector(".theme-toggle i");
	themeIcon.classList.toggle("fa-moon");
	themeIcon.classList.toggle("fa-sun");
}
