style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	display: flex;
	align-items: center;
	justify-content: center;
	height: 100dvh;
	background-color: rgb(27, 27, 27);
}

.carousel {
	max-width: 1400px;
	margin: 0 auto;
	overflow: hidden;
}

.carousel-items {
	display: flex;
	animation: carouselSlide 6s infinite forwards;
}

.carousel-item {
	min-width: 600px;
	width: 600px;
	height: 600px;
}

.carousel-item img {
	width: 100%;
	object-fit: cover;
}

.carousel:hover .carousel-items {
	animation-play-state: paused;
}

@keyframes carouselSlide {
	100% {
		transform: translateX(-2400px);
	}
}
