style:
body {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	background-color: black;
}

.container {
	width: 100vw;
	height: 100vh;
	text-align: center;
	overflow: hidden;
	position: relative;
}

.container .slider {
	position: absolute;
	width: 200px;
	height: 250px;
	top: 40%;
	left: calc(50% - 100px);
	transform-style: preserve-3d;
	transform: perspective(1000px);
}

.container .slider .item {
	position: absolute;
	inset: 0 0 0 0;
	transition: 0.4s ease;
	cursor: pointer;
	border-radius: 10px;
	overflow: hidden;
	box-shadow: 0 0 5px 0 #48abe0;
}

.container .slider .item:hover {
	box-shadow: none;
}

.container .slider .item img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	pointer-events: none;
}

@keyframes rotateSlider {
	from {
		transform: perspective(1000px) rotateY(0deg);
	}
	to {
		transform: perspective(1000px) rotateY(360deg);
	}
}

script:
const slider = document.querySelector(".slider");

const images = [
	"assets/img (1).jpg",
	"assets/img (2).jpg",
	"assets/img (3).jpg",
	"assets/img (4).jpg",
	"assets/img (5).jpg",
	"assets/img (6).jpg",
	"assets/img (7).jpg",
	"assets/img (8).jpg",
	"assets/img (9).jpg",
	"assets/img (10).jpg"
];

images.forEach((src, index) => {
	const item = document.createElement("div");
	item.classList.add("item");

	const img = document.createElement("img");
	img.src = src;
	img.alt = `Image ${index + 1}`;
	item.appendChild(img);
	slider.appendChild(item);
});

const items = document.querySelectorAll(".item");

items.forEach((item, index) => {
	let position = (index + 1 - 1) * (360 / items.length);
	item.style.transform = `rotateY(${position}deg) translateZ(550px)`;

	item.addEventListener("mouseenter", () => {
		slider.style.animationPlayState = "paused";
	});

	item.addEventListener("mouseleave", () => {
		slider.style.animationPlayState = "running";
	});
});

const rotateSlider = () => {
	slider.style.animation = "rotateSlider 40s linear infinite";
};

rotateSlider();
