style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

.slider-container {
	position: relative;
	width: 100%;
	height: 80vh;
	overflow: hidden;
	background: #1a1a1a;
}

.slider {
	position: absolute;
	width: 100%;
	height: 100%;
	display: flex;
	transition: transform 0.6s ease-in-out;
}

.slide {
	min-width: 100%;
	height: 100%;
	position: relative;
}

.slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	object-position: center;
}

.caption {
	position: absolute;
	bottom: 2rem;
	left: 50%;
	transform: translateX(-50%);
	color: white;
	text-align: center;
	background: rgba(0, 0, 0, 0.6);
	padding: 1rem 2rem;
	border-radius: 5px;
	font-family: Arial, sans-serif;
	max-width: 80%;
}

.nav-button {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	background: rgba(255, 255, 255, 0.2);
	border: none;
	color: white;
	padding: 1rem;
	cursor: pointer;
	transition: all 0.3s ease;
	backdrop-filter: blur(5px);
	border-radius: 50%;
	width: 40px;
	height: 40px;
	display: flex;
	align-items: center;
	justify-content: center;
}

.nav-button:hover {
	background: rgba(255, 255, 255, 0.3);
}

.prev {
	left: 2rem;
}

.next {
	right: 2rem;
}

.pagination {
	position: absolute;
	bottom: 1rem;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 0.5rem;
}

.pagination-dot {
	width: 12px;
	height: 12px;
	border-radius: 50%;
	background: rgba(255, 255, 255, 0.4);
	border: none;
	cursor: pointer;
	transition: all 0.3s ease;
}

.pagination-dot.active {
	background: rgba(255, 255, 255, 0.9);
	transform: scale(1.2);
}

@media (max-width: 768px) {
	.nav-button {
		width: 30px;
		height: 30px;
		padding: 0.5rem;
	}

	.caption {
		font-size: 0.9rem;
		padding: 0.8rem 1.5rem;
	}
}

script:
const slider = document.querySelector(".slider");
const prevButton = document.querySelector(".prev");
const nextButton = document.querySelector(".next");
const pagination = document.querySelector(".pagination");

const slides = [
	{ id: 101, caption: "Beautiful Landscape" },
	{ id: 102, caption: "Mountain View" },
	{ id: 103, caption: "City Skyline" },
	{ id: 104, caption: "Sunset Beach" },
	{ id: 105, caption: "Forest Pathway" }
];

let currentIndex = 0;
let autoSlideInterval;

function initSlider() {
	slides.forEach((slide) => {
		const slideDiv = document.createElement("div");
		slideDiv.className = "slide";
		slideDiv.innerHTML = `<img src="https://picsum.photos/id/${slide.id}/1600/900" alt="${slide.caption}"><div class="caption">${slide.caption}</div>`
		slider.appendChild(slideDiv);
	});

	slides.forEach((_, index) => {
		const dot = document.createElement("button");
		dot.className = "pagination-dot";
		if (index === 0) dot.classList.add("active");
		dot.addEventListener("click", () => moveToSlide(index));
		pagination.appendChild(dot);
	});

	startAutoSlide();
}

function moveToSlide(index) {
	currentIndex = index;
	const slideWidth = slider.children[0].offsetWidth;
	slider.style.transform = `translateX(-${currentIndex * slideWidth}px)`;

	document.querySelectorAll(".pagination-dot").forEach(dot => dot.classList.remove("active"));
	document.querySelectorAll(".pagination-dot")[currentIndex].classList.add("active");
}

function nextSlide() {
	currentIndex = (currentIndex + 1) % slides.length;
	moveToSlide(currentIndex);
}

function prevSlide() {
	currentIndex = (currentIndex - 1 + slides.length) % slides.length;
	moveToSlide(currentIndex);
}

function startAutoSlide() {
	autoSlideInterval = setInterval(nextSlide, 5000);
}

function resetAutoSlide() {
	clearInterval(autoSlideInterval);
	startAutoSlide();
}

prevButton.addEventListener("click", () => { prevSlide(); resetAutoSlide(); });
nextButton.addEventListener("click", () => { nextSlide(); resetAutoSlide(); });

window.addEventListener("load", initSlider);
window.addEventListener("resize", () => moveToSlide(currentIndex));
