style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: #0a0a0a;
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
}

.slider-container {
	position: relative;
	width: 100%;
	max-width: 1200px;
	padding: 40px 20px;
}

.slider-track {
	position: relative;
	height: 600px;
	overflow: hidden;
	border-radius: 20px;
}

.slider {
	position: absolute;
	width: 100%;
	height: 100%;
	transition: transform 0.8s cubic-bezier(0.23, 1, 0.32, 1);
}

.slide {
	position: absolute;
	width: 80%;
	height: 80%;
	left: 10%;
	top: 10%;
	opacity: 0.3;
	transform: scale(0.8);
	transition: all 0.8s cubic-bezier(0.23, 1, 0.32, 1);
	border-radius: 15px;
	overflow: hidden;
	box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
}

.slide.active {
	opacity: 1;
	transform: scale(1);
	z-index: 2;
}

.slide.prev {
	transform: translateY(-40%) scale(0.7);
	opacity: 0.4;
	z-index: 1;
}

.slide.next {
	transform: translateY(40%) scale(0.7);
	opacity: 0.4;
	z-index: 1;
}

.slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.content {
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	padding: 30px;
	background: linear-gradient(transparent, rgba(0, 0, 0, 0.9));
	color: white;
	transform: translateY(100%);
	transition: transform 0.5s ease;
}

.slide.active .content {
	transform: translateY(0);
}

.content h3 {
	font-size: 1.8rem;
	margin-bottom: 10px;
}

.nav-dots {
	position: absolute;
	right: 30px;
	top: 50%;
	transform: translateY(-50%);
	display: flex;
	flex-direction: column;
	gap: 15px;
	z-index: 3;
}

.nav-dot {
	width: 12px;
	height: 12px;
	border-radius: 50%;
	background: rgba(255, 255, 255, 0.3);
	border: 2px solid transparent;
	cursor: pointer;
	transition: all 0.3s ease;
}

.nav-dot.active {
	background: #fff;
	transform: scale(1.3);
	border-color: rgba(255, 255, 255, 0.8);
}

@media (max-width: 768px) {
	.slider-track {
		height: 400px;
	}

	.content h3 {
		font-size: 1.2rem;
	}

	.content {
		padding: 20px;
	}

	.nav-dots {
		right: 15px;
	}
}

script:
const slider = document.querySelector(".slider");
const navDots = document.querySelector(".nav-dots");

const slides = [
	{ id: 301, title: "Alpine Wonder" },
	{ id: 302, title: "Tropical Paradise" },
	{ id: 306, title: "Urban Jungle" },
	{ id: 304, title: "Desert Mirage" },
	{ id: 305, title: "Northern Lights" },
];

let currentIndex = 0;
let autoSlideInterval;

function createSlides() {
	slides.forEach((slide, index) => {
		const slideElement = document.createElement("div");
		slideElement.className = `slide ${index === 0 ? "active" : ""}`;
		slideElement.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/1200/800" alt="${slide.title}">
			<div class="content">
				<h3>${slide.title}</h3>
				<p>Explore the beauty of nature</p>
			</div>
		`;
		slider.appendChild(slideElement);

		const dot = document.createElement("div");
		dot.className = `nav-dot ${index === 0 ? "active" : ""}`;
		dot.addEventListener("click", () => goToSlide(index));
		navDots.appendChild(dot);
	});
}

function updateClasses() {
	document.querySelectorAll(".slide").forEach((slide, index) => {
		slide.className = "slide";
		if (index === currentIndex) {
			slide.classList.add("active");
		} else if (index === currentIndex - 1) {
			slide.classList.add("prev");
		} else if (index === currentIndex + 1) {
			slide.classList.add("next");
		}
	});

	document.querySelectorAll(".nav-dot").forEach((dot, index) => {
		dot.classList.toggle("active", index === currentIndex);
	});
}

function goToSlide(index) {
	currentIndex = (index + slides.length) % slides.length;
	updateClasses();
}

function nextSlide() {
	currentIndex = (currentIndex + 1) % slides.length;
	updateClasses();
}

function prevSlide() {
	currentIndex = (currentIndex - 1 + slides.length) % slides.length;
	updateClasses();
}

function startAutoSlide() {
	autoSlideInterval = setInterval(nextSlide, 6000);
}

function handleHover() {
	clearInterval(autoSlideInterval);
}

// Initialize
function init() {
	createSlides();
	startAutoSlide();

	// Add hover pause
	slider.addEventListener("mouseenter", handleHover);
	slider.addEventListener("mouseleave", startAutoSlide);
}

// Handle keyboard navigation
document.addEventListener("keydown", (e) => {
	if (e.key === "ArrowRight") nextSlide();
	if (e.key === "ArrowLeft") prevSlide();
});

init();
