style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: #1a1a1a;
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	perspective: 1000px;
	overflow: hidden;
}

.carousel-container {
	position: relative;
	width: 100%;
	max-width: 1200px;
	height: 70vh;
}

.carousel {
	position: absolute;
	display: flex;
	align-items: center;
	justify-content: center;
	width: 100%;
	height: 100%;
	transform-style: preserve-3d;
	transition: transform 1s cubic-bezier(0.23, 1, 0.32, 1);
}

.slide {
	position: absolute;
	width: 300px;
	height: 400px;
	border-radius: 15px;
	overflow: hidden;
	box-shadow: 0 25px 50px rgba(0, 0, 0, 0.3);
	cursor: grab;
	transition: all 0.8s ease;
	transform-origin: center center;
}

.slide img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.slide-content {
	position: absolute;
	bottom: 0;
	left: 0;
	right: 0;
	padding: 20px;
	background: linear-gradient(transparent, rgba(0, 0, 0, 0.9));
	color: white;
	transform: translateY(100%);
	transition: transform 0.4s ease;
}

.slide.active .slide-content {
	transform: translateY(0);
}

.nav-buttons {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	width: 100%;
	display: flex;
	justify-content: space-between;
	padding: 0 20px;
	pointer-events: none;
}

.nav-button {
	pointer-events: all;
	background: rgba(255, 255, 255, 0.9);
	border: none;
	width: 50px;
	height: 50px;
	border-radius: 50%;
	cursor: pointer;
	transition: all 0.3s ease;
	display: grid;
	place-items: center;
	box-shadow: 0 4px 15px rgba(0, 0, 0, 0.2);
}

.nav-button:hover {
	transform: scale(1.1);
	background: #fff;
}

.radial-pagination {
	position: absolute;
	bottom: 20px;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 10px;
}

.radial-dot {
	width: 12px;
	height: 12px;
	border-radius: 50%;
	background: rgba(255, 255, 255, 0.3);
	cursor: pointer;
	transition: all 0.3s ease;
	position: relative;
}

.radial-dot.active {
	background: rgba(255, 255, 255, 0.9);
	transform: scale(1.3);
}

@media (max-width: 768px) {
	.carousel-container {
		height: 50vh;
	}

	.slide {
		width: 200px;
		height: 300px;
	}

	.nav-button {
		width: 40px;
		height: 40px;
	}
}

script:
const carousel = document.querySelector(".carousel");
const prevBtn = document.querySelector(".prev");
const nextBtn = document.querySelector(".next");
const pagination = document.querySelector(".radial-pagination");

const slides = [
	{ id: 600, title: "Mountain Range" },
	{ id: 602, title: "Tropical Beach" },
	{ id: 603, title: "Modern City" },
	{ id: 604, title: "Desert Oasis" },
	{ id: 605, title: "Northern Lights" },
];

let currentAngle = 0;
let isDragging = false;
let startX = 0;
let autoRotateInterval;

function createSlides() {
	const angleIncrement = 360 / slides.length;

	slides.forEach((slide, index) => {
		const slideElement = document.createElement("div");
		slideElement.className = "slide";
		slideElement.innerHTML = `
			<img src="https://picsum.photos/id/${slide.id}/600/800" alt="${slide.title}">
			<div class="slide-content">${slide.title}</div>
		`;

		let position = index * (360 / slides.length);
		slideElement.style.transform = `rotateY(${position}deg) translateZ(550px)`;

		carousel.appendChild(slideElement);

		// Create pagination dots
		const dot = document.createElement("div");
		dot.className = "radial-dot";
		dot.addEventListener("click", () => rotateToIndex(index));
		pagination.appendChild(dot);
	});

	updateActiveState();
}

function rotateCarousel(angle) {
	currentAngle = angle;
	carousel.style.transform = `rotateY(${currentAngle}deg)`;
	updateActiveState();
}

function rotateToIndex(index) {
	const targetAngle = -index * (360 / slides.length);
	rotateCarousel(targetAngle);
}

function nextSlide() {
	currentAngle -= 360 / slides.length;
	rotateCarousel(currentAngle);
}

function prevSlide() {
	currentAngle += 360 / slides.length;
	rotateCarousel(currentAngle);
}

function updateActiveState() {
	const activeIndex = Math.round((-currentAngle % 360) / (360 / slides.length));

	document.querySelectorAll(".slide").forEach((slide, index) => {
		const angle = index * (360 / slides.length) + currentAngle;

		slide.style.transform = `
			rotateY(${index * (360 / slides.length)}deg)
			translateZ(400px)
		`;
		slide.classList.toggle("active", index === activeIndex);
	});

	document.querySelectorAll(".radial-dot").forEach((dot, index) => {
		dot.classList.toggle("active", index === activeIndex);
	});
}

// Event listeners
prevBtn.addEventListener("click", prevSlide);
nextBtn.addEventListener("click", nextSlide);
window.addEventListener("resize", updateActiveState);

// Initialize
createSlides();
