style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--icon-bg: #f5f5f5;
	--hover-bg: #e0e0e0;
}

[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #ffffff;
	--icon-bg: #2d2d2d;
	--hover-bg: #3d3d3d;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.social-container {
	display: flex;
	gap: 1.2rem;
	padding: 2rem;
	flex-wrap: wrap;
	justify-content: center;
}

.social-link {
	width: 48px;
	height: 48px;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 12px;
	background: var(--icon-bg);
	text-decoration: none;
	position: relative;
	overflow: hidden;
}

.social-link i {
	font-size: 1.2rem;
	color: var(--text);
	z-index: 1;
}

.social-link::before {
	content: "";
	position: absolute;
	width: 100%;
	height: 100%;
	background: var(--hover-bg);
	opacity: 0;
	border-radius: 12px;
}

.social-link:hover::before {
	opacity: 1;
}

.social-link:hover i {
	transform: scale(1.1);
}

.theme-switch {
	position: fixed;
	top: 24px;
	right: 24px;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	background: var(--icon-bg);
	border: none;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
}

.theme-switch i {
	font-size: 1.1rem;
	color: var(--text);
}

@media (max-width: 480px) {
	.social-container {
		padding: 1rem;
		gap: 1rem;
	}

	.social-link {
		width: 44px;
		height: 44px;
	}
}

script:
function toggleTheme() {
	if (document.body.getAttribute("data-theme") === "dark") {
		document.body.removeAttribute("data-theme");
	} else {
		document.body.setAttribute("data-theme", "dark");
	}
	const icon = document.querySelector(".theme-switch i");
	icon.classList.toggle("fa-moon");
	icon.classList.toggle("fa-sun");
}
