style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--hover: #4a90e2;
	--underline: rgba(74, 144, 226, 0.2);
}

[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #ffffff;
	--hover: #7fb6ff;
	--underline: rgba(127, 182, 255, 0.2);
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.social-container {
	display: flex;
	gap: 2rem;
	padding: 2rem;
	flex-wrap: wrap;
	justify-content: center;
}

.social-link {
	display: flex;
	align-items: center;
	gap: 0.8rem;
	text-decoration: none;
	color: var(--text);
	position: relative;
	padding: 0.5rem 0;
}

.social-link i {
	font-size: 1.4rem;
	width: 32px;
	text-align: center;
}

.social-link span {
	font-weight: 500;
	position: relative;
}

.social-link span::after {
	content: "";
	position: absolute;
	width: 0;
	height: 2px;
	bottom: -4px;
	left: 0;
	background: var(--hover);
	transition: width 0.3s ease;
}

.social-link:hover span::after {
	width: 100%;
}

.social-link:hover {
	color: var(--hover);
}

.theme-toggle {
	position: fixed;
	top: 2rem;
	right: 2rem;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	background: var(--bg);
	border: 2px solid var(--text);
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;
}

.theme-toggle i {
	color: var(--text);
	font-size: 1.2rem;
}

@media (max-width: 768px) {
	.social-container {
		flex-direction: column;
		align-items: flex-start;
		padding: 1.5rem;
	}

	.social-link {
		padding: 0.3rem 0;
	}
}

script:
function toggleTheme() {
	if (document.body.getAttribute("data-theme") === "dark") {
		document.body.removeAttribute("data-theme");
	} else {
		document.body.setAttribute("data-theme", "dark");
	}
	const icon = document.querySelector(".theme-toggle i");
	icon.classList.toggle("fa-moon");
	icon.classList.toggle("fa-sun");
}
