style:
:root {
	--bg-color: #ffffff;
	--text-color: #333333;
	--icon-bg: #f0f0f0;
	--icon-hover: #e0e0e0;
	--theme-toggle-bg: #f0f0f0;
}

[data-theme="dark"] {
	--bg-color: #1a1a1a;
	--text-color: #ffffff;
	--icon-bg: #2d2d2d;
	--icon-hover: #3d3d3d;
	--theme-toggle-bg: #2d2d2d;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: background-color 0.3s ease, color 0.3s ease;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background-color: var(--bg-color);
	color: var(--text-color);
	font-family: 'Arial', sans-serif;
}

.social-container {
	display: flex;
	gap: 1.5rem;
	flex-wrap: wrap;
	justify-content: center;
	padding: 2rem;
}

.social-link {
	width: 50px;
	height: 50px;
	border-radius: 50%;
	display: flex;
	justify-content: center;
	align-items: center;
	text-decoration: none;
	background-color: var(--icon-bg);
	position: relative;
	transition: transform 0.3s ease;
}

.social-link i {
	font-size: 1.4rem;
	color: var(--text-color);
	transition: color 0.3s ease;
}

.social-link:hover {
	transform: translateY(-3px);
	background-color: var(--icon-hover);
}

.social-link.facebook:hover i { color: #1877f2; }
.social-link.twitter:hover i { color: #1da1f2; }
.social-link.instagram:hover i { color: #e4405f; }
.social-link.linkedin:hover i { color: #0a66c2; }
.social-link.github:hover i { color: #6e5494; }

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	width: 40px;
	height: 40px;
	border-radius: 50%;
	background-color: var(--theme-toggle-bg);
	border: none;
	cursor: pointer;
	display: flex;
	justify-content: center;
	align-items: center;
}

.theme-toggle i {
	font-size: 1.2rem;
	color: var(--text-color);
}

@media (max-width: 480px) {
	.social-container {
		flex-direction: column;
	}
}

script:
const themeToggle = document.querySelector('.theme-toggle');
const body = document.body;
let isDark = false;

themeToggle.addEventListener('click', () => {
	isDark = !isDark;
	body.setAttribute('data-theme', isDark ? 'dark' : '');
	themeToggle.innerHTML = isDark 
		? '<i class="fas fa-sun"></i>' 
		: '<i class="fas fa-moon"></i>';
});

const prefersDark = window.matchMedia('(prefers-color-scheme: dark)');
if (prefersDark.matches) {
	body.setAttribute('data-theme', 'dark');
	themeToggle.innerHTML = '<i class="fas fa-sun"></i>';
	isDark = true;
}
