style:
:root {
	--primary-bg: #f8f9fa;
	--secondary-bg: #ffffff;
	--text-color: #2d3436;
	--accent: #0984e3;
	--toggle-bg: #dfe6e9;
}

[data-theme="dark"] {
	--primary-bg: #2d3436;
	--secondary-bg: #3b4346;
	--text-color: #f8f9fa;
	--accent: #74b9ff;
	--toggle-bg: #3b4346;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.3s ease;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--primary-bg);
	font-family: "Segoe UI", sans-serif;
}

.social-container {
	display: flex;
	gap: 1.2rem;
	padding: 2rem;
	background: var(--secondary-bg);
	border-radius: 15px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.social-link {
	position: relative;
	width: 50px;
	height: 50px;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 12px;
	background: var(--secondary-bg);
	cursor: pointer;
	overflow: hidden;
	text-decoration: none;
}

.social-link::before {
	content: "";
	position: absolute;
	width: 120%;
	height: 120%;
	background: linear-gradient(45deg, var(--accent), transparent);
	transform: rotate(45deg);
	opacity: 0;
	transition: opacity 0.3s;
}

.social-link:hover::before {
	opacity: 0.2;
}

.social-link i {
	font-size: 1.4rem;
	color: var(--text-color);
	z-index: 1;
}

.social-link:hover {
	transform: translateY(-5px);
}

.theme-toggle {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 60px;
	height: 30px;
	border-radius: 15px;
	background: var(--toggle-bg);
	cursor: pointer;
	border: none;
}

.theme-toggle::after {
	content: "";
	position: absolute;
	left: 3px;
	top: 3px;
	width: 24px;
	height: 24px;
	background: var(--text-color);
	border-radius: 50%;
	transition: transform 0.3s;
}

[data-theme="dark"] .theme-toggle::after {
	transform: translateX(30px);
}

.tooltip {
	position: absolute;
	bottom: -35px;
	background: var(--secondary-bg);
	color: var(--text-color);
	padding: 5px 10px;
	border-radius: 6px;
	font-size: 0.9rem;
	opacity: 0;
	pointer-events: none;
	transition: opacity 0.2s;
	box-shadow: 0 3px 10px rgba(0, 0, 0, 0.1);
}

.social-link:hover .tooltip {
	opacity: 1;
}

@media (max-width: 480px) {
	.social-container {
		flex-direction: column;
		padding: 1.5rem;
		border-radius: 20px;
	}

	.theme-toggle {
		top: 20px;
		right: 20px;
	}
}

script:
const toggle = document.querySelector(".theme-toggle");
const body = document.body;
let isDark = localStorage.getItem("theme") === "dark";

if (isDark) body.setAttribute("data-theme", "dark");

toggle.addEventListener("click", () => {
	isDark = !isDark;
	body.setAttribute("data-theme", isDark ? "dark" : "");
	localStorage.setItem("theme", isDark ? "dark" : "light");
});

const mediaQuery = window.matchMedia("(prefers-color-scheme: dark)");
const systemDark = mediaQuery.matches;

if (!localStorage.getItem("theme") && systemDark) {
	body.setAttribute("data-theme", "dark");
	isDark = true;
}
