style:
:root {
	--bg: #e0e5ec;
	--text: #2d3436;
	--shadow-light: 20px 20px 60px #bec3c9;
	--shadow-dark: -20px -20px 60px #ffffff;
}

[data-theme="dark"] {
	--bg: #1a1a1a;
	--text: #ffffff;
	--shadow-light: 20px 20px 60px #0d0d0d;
	--shadow-dark: -20px -20px 60px #272727;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: var(--bg);
	font-family: "Inter", sans-serif;
}

.social-container {
	display: grid;
	grid-template-columns: repeat(5, 1fr);
	gap: 2rem;
	padding: 3rem;
	border-radius: 30px;
	box-shadow: var(--shadow-light), var(--shadow-dark);
}

.social-link {
	width: 60px;
	height: 60px;
	display: flex;
	align-items: center;
	justify-content: center;
	border-radius: 15px;
	text-decoration: none;
	position: relative;
	box-shadow: 8px 8px 16px rgba(0, 0, 0, 0.1);
	background: var(--bg);
}

.social-link i {
	font-size: 1.6rem;
	color: var(--text);
	z-index: 1;
}

.social-link:hover {
	transform: translateY(-8px) scale(1.1);
	box-shadow: 15px 15px 30px rgba(0, 0, 0, 0.15);
}

.social-link::after {
	content: "";
	position: absolute;
	width: 100%;
	height: 100%;
	border-radius: 15px;
	background: linear-gradient(145deg, rgba(255, 255, 255, 0.1), rgba(0, 0, 0, 0.05));
	opacity: 0;
	transition: opacity 0.3s;
}

.social-link:hover::after {
	opacity: 1;
}

.theme-switch {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 60px;
	height: 34px;
	opacity: 0;
	z-index: 2;
	cursor: pointer;
}

.theme-label {
	position: fixed;
	top: 30px;
	right: 30px;
	width: 60px;
	height: 34px;
	background: var(--bg);
	border-radius: 17px;
	box-shadow: inset 5px 5px 10px rgba(0, 0, 0, 0.1), inset -5px -5px 10px rgba(255, 255, 255, 0.1);
}

.theme-label::before {
	content: "";
	position: absolute;
	width: 26px;
	height: 26px;
	border-radius: 50%;
	background: var(--text);
	top: 4px;
	left: 4px;
	transition: transform 0.4s;
}

.theme-switch:checked ~ .theme-label::before {
	transform: translateX(26px);
}

@media (max-width: 768px) {
	.social-container {
		grid-template-columns: repeat(3, 1fr);
		padding: 2rem;
		gap: 1.5rem;
	}
}

@media (max-width: 480px) {
	.social-container {
		grid-template-columns: repeat(2, 1fr);
		padding: 1.5rem;
		gap: 1rem;
	}

	.social-link {
		width: 50px;
		height: 50px;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const themeToggle = document.getElementById("themeToggle");
	themeToggle.addEventListener("change", () => {
		if (themeToggle.checked) {
			document.body.setAttribute("data-theme", "dark");
		} else {
			document.body.removeAttribute("data-theme");
		}
	});
});
