style:
/* Reset styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: background 0.3s, color 0.3s;
}

body {
	font-family: Arial, sans-serif;
	background: var(--bg);
	color: var(--text);
	display: flex;
	height: 100vh;
}

/* Light & Dark Theme Variables */
:root {
	--bg: #f5f5f5;
	--text: #333;
	--sidebar-bg: #fff;
	--content-bg: #fff;
	--border: #ddd;
	--primary: #3498db;
	--hover: rgba(0, 0, 0, 0.1);
}

.dark-mode {
	--bg: #1e1e1e;
	--text: #f5f5f5;
	--sidebar-bg: #292929;
	--content-bg: #1e1e1e;
	--border: #444;
	--primary: #4db8ff;
	--hover: rgba(255, 255, 255, 0.1);
}

/* Sidebar */
.sidebar {
	width: 250px;
	background: var(--sidebar-bg);
	padding: 20px;
	border-right: 1px solid var(--border);
	display: flex;
	flex-direction: column;
	align-items: center;
}

.sidebar button {
	background: none;
	border: none;
	width: 100%;
	padding: 15px;
	font-size: 18px;
	display: flex;
	align-items: center;
	gap: 10px;
	cursor: pointer;
	color: var(--text);
	transition: 0.3s;
	text-align: left;
}

.sidebar button i {
	font-size: 20px;
}

.sidebar button:hover,
.sidebar button.active {
	background: var(--hover);
	color: var(--primary);
	border-radius: 5px;
}

/* Theme Toggle Button */
.theme-toggle {
	background: var(--sidebar-bg);
	border: 1px solid var(--border);
	padding: 10px 15px;
	border-radius: 20px;
	cursor: pointer;
	font-size: 16px;
	color: var(--text);
	display: flex;
	align-items: center;
	gap: 8px;
	margin-top: auto;
}

.theme-toggle:hover {
	background: var(--hover);
}

/* Content Area */
.content {
	flex: 1;
	background: var(--content-bg);
	padding: 40px;
	text-align: center;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 24px;
	font-weight: bold;
}

.content div {
	display: none;
}

.content div.active {
	display: block;
}

/* Responsive */
@media (max-width: 768px) {
	body {
		flex-direction: column;
	}

	.sidebar {
		width: 100%;
		flex-direction: row;
		justify-content: space-around;
		border-right: none;
		border-bottom: 1px solid var(--border);
	}

	.sidebar button {
		flex: 1;
		justify-content: center;
	}

	.theme-toggle {
		position: absolute;
		right: 10px;
		top: 10px;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const tabButtons = document.querySelectorAll(".sidebar button:not(.theme-toggle)");
	const tabPanels = document.querySelectorAll(".content > div");
	const themeToggle = document.querySelector(".theme-toggle");
	const body = document.body;

	// Handle Tab Switching
	tabButtons.forEach((button) => {
		button.addEventListener("click", function () {
			tabButtons.forEach((btn) => btn.classList.remove("active"));
			tabPanels.forEach((panel) => panel.classList.remove("active"));

			this.classList.add("active");
			document.getElementById(this.getAttribute("data-tab")).classList.add("active");
		});
	});

	// Theme Toggle Function
	themeToggle.addEventListener("click", function () {
		body.classList.toggle("dark-mode");

		if (body.classList.contains("dark-mode")) {
			themeToggle.innerHTML = '<i class="fa-solid fa-sun"></i> Light Mode';
		} else {
			themeToggle.innerHTML = '<i class="fa-solid fa-moon"></i> Dark Mode';
		}
	});
});
