style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, -apple-system, sans-serif;
}

body {
	background: #f8fafc;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 2rem;
}

.tab-system {
	background: #fff;
	width: 100%;
	max-width: 1200px;
	border-radius: 1.5rem;
	box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.1);
	overflow: hidden;
	display: grid;
	grid-template-columns: 260px 1fr;
	min-height: 500px;
}

.tab-nav {
	background: #f9fafb;
	padding: 2rem;
	border-right: 1px solid #e5e7eb;
}

.tab-button {
	width: 100%;
	padding: 1rem 1.5rem;
	margin-bottom: 0.75rem;
	border: none;
	background: none;
	text-align: left;
	cursor: pointer;
	border-radius: 0.75rem;
	display: flex;
	align-items: center;
	gap: 1rem;
	color: #4b5563;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
	position: relative;
}

.tab-button:hover {
	background: #f3f4f6;
	transform: translateX(5px);
}

.tab-button.active {
	background: #fff;
	color: #7c3aed;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.tab-button.active::before {
	content: "";
	position: absolute;
	left: -1.5rem;
	height: 100%;
	width: 4px;
	background: #7c3aed;
	border-radius: 4px;
	animation: slideIn 0.3s ease-out;
}

.tab-content {
	padding: 3rem;
	opacity: 0;
	transform: translateY(20px);
	animation: contentReveal 0.4s ease forwards;
	display: none;
}

.tab-content.active {
	display: block;
}

.tab-icon {
	width: 24px;
	text-align: center;
}

.tab-badge {
	background: #7c3aed;
	color: white;
	padding: 0.25rem 0.75rem;
	border-radius: 999px;
	font-size: 0.75rem;
	margin-left: auto;
}

@keyframes slideIn {
	from {
		transform: translateX(-10px);
		opacity: 0;
	}
	to {
		transform: translateX(0);
		opacity: 1;
	}
}

@keyframes contentReveal {
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tab-system {
		grid-template-columns: 1fr;
		min-height: auto;
	}

	.tab-nav {
		display: flex;
		overflow-x: auto;
		padding: 1rem;
		border-right: none;
		border-bottom: 1px solid #e5e7eb;
	}

	.tab-button {
		flex: 0 0 auto;
		width: 200px;
		margin-bottom: 0;
		margin-right: 0.5rem;
	}

	.tab-button.active::before {
		bottom: -1rem;
		left: 0;
		height: 4px;
		width: 100%;
	}
}

@media (max-width: 480px) {
	body {
		padding: 1rem;
	}

	.tab-content {
		padding: 2rem 1.5rem;
	}

	.tab-button {
		width: 180px;
		padding: 1rem;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const tabs = document.querySelectorAll(".tab-button");
	const contents = document.querySelectorAll(".tab-content");

	tabs.forEach((tab) => {
		tab.addEventListener("click", function () {
			tabs.forEach((t) => t.classList.remove("active"));
			contents.forEach((c) => c.classList.remove("active"));

			this.classList.add("active");
			const targetContent = document.getElementById(this.dataset.target);
			targetContent.classList.add("active");

			void targetContent.offsetWidth;
			targetContent.style.animation = "none";
			targetContent.offsetHeight;
			targetContent.style.animation = null;
		});
	});

	document.addEventListener("keydown", (e) => {
		const activeTab = document.querySelector(".tab-button.active");
		const tabsArray = Array.from(tabs);
		const currentIndex = tabsArray.indexOf(activeTab);

		if (e.key === "ArrowRight" || e.key === "ArrowDown") {
			const nextTab = tabsArray[(currentIndex + 1) % tabsArray.length];
			nextTab.click();
		} else if (e.key === "ArrowLeft" || e.key === "ArrowUp") {
			const prevTab = tabsArray[(currentIndex - 1 + tabsArray.length) % tabsArray.length];
			prevTab.click();
		}
	});
});
