style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", system-ui, sans-serif;
}

body {
	background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 20px;
}

.card-tabs {
	background: #ffffff;
	width: 100%;
	max-width: 1000px;
	border-radius: 20px;
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.1);
	overflow: hidden;
}

.tab-header {
	display: flex;
	position: relative;
	padding: 20px;
	gap: 15px;
	background: #f8f9fa;
	border-bottom: 1px solid #e9ecef;
}

.tab-label {
	padding: 15px 30px;
	border: none;
	background: transparent;
	cursor: pointer;
	display: flex;
	align-items: center;
	gap: 12px;
	color: #6c757d;
	border-radius: 10px;
	transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
	position: relative;
	z-index: 1;
}

.tab-label i {
	font-size: 1.2em;
	width: 25px;
}

.tab-label:hover {
	color: #495057;
	transform: translateY(-2px);
}

.tab-label.active {
	color: #0d6efd;
}

.tab-indicator {
	position: absolute;
	bottom: -1px;
	height: 3px;
	background: #0d6efd;
	transition: all 0.3s ease;
	border-radius: 3px 3px 0 0;
}

.tab-content-wrapper {
	padding: 30px;
	position: relative;
}

.tab-pane {
	display: none;
	animation: slideIn 0.4s ease-out forwards;
}

.tab-pane.active {
	display: block;
}

.tab-pane h2 {
	color: #212529;
	margin-bottom: 20px;
	font-size: 1.8em;
}

.tab-pane p {
	color: #495057;
	line-height: 1.7;
	max-width: 600px;
}

@keyframes slideIn {
	from {
		opacity: 0;
		transform: translateY(20px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tab-header {
		flex-wrap: wrap;
		gap: 10px;
		padding: 15px;
	}

	.tab-label {
		flex: 1 1 45%;
		justify-content: center;
		padding: 12px 15px;
	}

	.tab-indicator {
		display: none;
	}

	.tab-content-wrapper {
		padding: 25px 20px;
	}
}

@media (max-width: 480px) {
	.tab-label {
		flex: 1 1 100%;
	}

	.tab-label i {
		display: none;
	}

	body {
		padding: 15px;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const tabs = document.querySelectorAll(".tab-label");
	const panes = document.querySelectorAll(".tab-pane");
	const indicator = document.querySelector(".tab-indicator");

	function updateIndicator(target) {
		const activeTab = document.querySelector(".tab-label.active");
		indicator.style.width = `${activeTab.offsetWidth}px`;
		indicator.style.left = `${activeTab.offsetLeft}px`;
	}

	function switchTab(clickedTab) {
		tabs.forEach((tab) => tab.classList.remove("active"));
		panes.forEach((pane) => pane.classList.remove("active"));

		clickedTab.classList.add("active");
		document.getElementById(clickedTab.dataset.target).classList.add("active");

		updateIndicator(clickedTab);
	}

	updateIndicator(document.querySelector(".tab-label.active"));

	tabs.forEach((tab) => {
		tab.addEventListener("click", () => {
			switchTab(tab);
		});
	});

	window.addEventListener("resize", () => {
		updateIndicator(document.querySelector(".tab-label.active"));
	});
});
