style:
:root {
	--primary: #7c3aed;
	--secondary: #ec4899;
	--background: #0f172a;
	--surface: rgba(255, 255, 255, 0.05);
	--text: #f8fafc;
}

[data-theme="light"] {
	--background: #f1f5f9;
	--surface: rgba(255, 255, 255, 0.7);
	--text: #1e293b;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: var(--background);
	color: var(--text);
	min-height: 100vh;
	display: grid;
	place-items: center;
	padding: 2rem;
	transition: background 0.3s ease;
}

.container {
	width: 100%;
	max-width: 1200px;
	position: relative;
}

.tabs-wrapper {
	background: var(--surface);
	backdrop-filter: blur(16px);
	border-radius: 24px;
	box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
	border: 1px solid rgba(255, 255, 255, 0.1);
	overflow: hidden;
}

.tab-nav {
	display: flex;
	gap: 1rem;
	padding: 1.5rem;
	position: relative;
	background: linear-gradient(45deg, rgba(124, 58, 237, 0.1), rgba(236, 72, 153, 0.1));
}

.tab-btn {
	padding: 1rem 2rem;
	border: none;
	background: rgba(255, 255, 255, 0.1);
	color: var(--text);
	cursor: pointer;
	border-radius: 12px;
	display: flex;
	gap: 0.75rem;
	align-items: center;
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.tab-btn::before {
	content: "";
	position: absolute;
	inset: 0;
	background: linear-gradient(45deg, var(--primary), var(--secondary));
	opacity: 0;
	transition: opacity 0.3s ease;
}

.tab-btn:hover {
	transform: translateY(-2px);
	box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
}

.tab-btn.active-tab {
	background: rgba(255, 255, 255, 0.2);
}

.tab-btn.active-tab::before {
	opacity: 0.2;
}

.tab-content {
	padding: 2rem;
	display: none;
	grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
	gap: 2rem;
	animation: fadeIn 0.4s ease;
}

.tab-content.active-content {
	display: grid;
}

.card {
	background: var(--surface);
	padding: 1.5rem;
	border-radius: 16px;
	border: 1px solid rgba(255, 255, 255, 0.1);
	transition: transform 0.3s ease;
}

.card:hover {
	transform: translateY(-5px);
}

.card h3 {
	color: var(--primary);
	margin-bottom: 1rem;
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	background: var(--surface);
	border: 1px solid rgba(255, 255, 255, 0.1);
	padding: 1rem;
	border-radius: 12px;
	cursor: pointer;
	backdrop-filter: blur(10px);
}

@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

@media (max-width: 768px) {
	.tab-nav {
		flex-direction: column;
	}

	.tab-btn {
		justify-content: center;
	}

	.tab-content {
		grid-template-columns: 1fr;
	}
}

script:
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

function toggleTheme() {
	const isDark = body.getAttribute("data-theme") !== "light";
	body.setAttribute("data-theme", isDark ? "light" : "");
	themeToggle.innerHTML = isDark ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
	localStorage.setItem("theme", isDark ? "light" : "dark");
}

const savedTheme = localStorage.getItem("theme");
if (savedTheme === "light") {
	body.setAttribute("data-theme", "light");
	themeToggle.innerHTML = '<i class="fas fa-sun"></i>';
}

themeToggle.addEventListener("click", toggleTheme);

document.querySelectorAll(".tab-btn").forEach((button) => {
	button.addEventListener("click", () => {
		document.querySelectorAll(".tab-btn").forEach((b) => b.classList.remove("active-tab"));
		document.querySelectorAll(".tab-content").forEach((c) => c.classList.remove("active-content"));

		button.classList.add("active-tab");
		document.getElementById(button.dataset.tab).classList.add("active-content");
	});
});

document.querySelectorAll(".card").forEach((card) => {
	card.addEventListener("mousemove", (e) => {
		const rect = card.getBoundingClientRect();
		const x = e.clientX - rect.left;
		const y = e.clientY - rect.top;
		card.style.setProperty("--x", `${x}px`);
		card.style.setProperty("--y", `${y}px`);
	});
});
