style:
:root {
    --primary: #6366f1;
    --secondary: #a855f7;
    --background: #ffffff;
    --text: #1f2937;
    --surface: #f8fafc;
    --blob-size: 90px;
}

[data-theme="dark"] {
    --primary: #818cf8;
    --secondary: #c084fc;
    --background: #0f172a;
    --text: #f8fafc;
    --surface: #1e293b;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Segoe UI", sans-serif;
}

body {
    background: var(--background);
    color: var(--text);
    min-height: 100vh;
    display: grid;
    place-items: center;
    padding: 2rem;
    transition: background 0.4s ease;
}

.tabs-container {
    width: 100%;
    max-width: 800px;
    position: relative;
}

.tabs-nav {
    display: flex;
    gap: 1rem;
    padding: 1rem;
    background: var(--surface);
    border-radius: 1.5rem;
    position: relative;
    overflow: hidden;
}

.tab-blob {
    position: absolute;
    background: linear-gradient(45deg, var(--primary), var(--secondary));
    width: var(--blob-size);
    height: var(--blob-size);
    border-radius: 50%;
    filter: blur(20px);
    opacity: 0.15;
    transition: all 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.tab-btn {
    padding: 1rem 2rem;
    border: none;
    background: transparent;
    color: var(--text);
    cursor: pointer;
    z-index: 1;
    border-radius: 1rem;
    display: flex;
    gap: 0.75rem;
    align-items: center;
    transition: color 0.3s ease;
    position: relative;
}

.tab-btn.active-tab {
    color: var(--primary);
    font-weight: 600;
}

.tab-content {
    padding: 2rem;
    margin-top: 1rem;
    background: var(--surface);
    border-radius: 1.5rem;
    display: none;
    opacity: 0;
    animation: contentIn 0.4s ease forwards;
}

.tab-content.active-content {
    display: block;
}

.theme-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
    background: var(--surface);
    border: none;
    padding: 1rem;
    border-radius: 1rem;
    cursor: pointer;
    display: flex;
    gap: 0.5rem;
    align-items: center;
    transition: transform 0.3s ease;
}

.theme-toggle:hover {
    transform: rotate(15deg);
}

@keyframes contentIn {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

@media (max-width: 640px) {
    .tabs-nav {
        flex-direction: column;
    }

    .tab-blob {
        display: none;
    }

    .tab-btn {
        justify-content: center;
    }
}

script:
// Theme Management
const themeToggle = document.querySelector(".theme-toggle");
const body = document.body;

function toggleTheme() {
    const isDark = body.getAttribute("data-theme") === "dark";
    body.setAttribute("data-theme", isDark ? "light" : "dark");
    themeToggle.innerHTML = isDark ? '<i class="fas fa-moon"></i>' : '<i class="fas fa-sun"></i>';
    localStorage.setItem("theme", isDark ? "light" : "dark");
}

// Load saved theme
const savedTheme = localStorage.getItem("theme");
if (savedTheme) {
    body.setAttribute("data-theme", savedTheme);
    themeToggle.innerHTML = savedTheme === "dark" ? '<i class="fas fa-sun"></i>' : '<i class="fas fa-moon"></i>';
}

themeToggle.addEventListener("click", toggleTheme);

// Tab Functionality
const tabButtons = document.querySelectorAll(".tab-btn");
const tabContents = document.querySelectorAll(".tab-content");
const tabBlob = document.querySelector(".tab-blob");

function moveBlob(button) {
    const buttonRect = button.getBoundingClientRect();
    const navRect = button.parentElement.getBoundingClientRect();
    const blobSize = parseInt(getComputedStyle(document.documentElement).getPropertyValue("--blob-size"));
    tabBlob.style.left = `${buttonRect.left - navRect.left + buttonRect.width / 2 - blobSize / 2}px`;
    tabBlob.style.width = `${blobSize}px`;
    tabBlob.style.height = `${blobSize}px`;
}

function activateTab(button) {
    tabButtons.forEach((btn) => btn.classList.remove("active-tab"));
    tabContents.forEach((content) => content.classList.remove("active-content"));
    button.classList.add("active-tab");
    document.getElementById(button.dataset.tab).classList.add("active-content");
    moveBlob(button);
}

// Initialize
activateTab(document.querySelector(".active-tab"));

// Event Listeners
tabButtons.forEach((button) => {
    button.addEventListener("click", () => activateTab(button));
});

// Resize Handler
window.addEventListener("resize", () => {
    moveBlob(document.querySelector(".active-tab"));
});

// Keyboard Navigation
document.addEventListener("keydown", (e) => {
    const tabs = Array.from(tabButtons);
    const currentIndex = tabs.findIndex((t) => t.classList.contains("active-tab"));
    if (e.key === "ArrowRight" && currentIndex < tabs.length - 1) {
        activateTab(tabs[currentIndex + 1]);
    }
    if (e.key === "ArrowLeft" && currentIndex > 0) {
        activateTab(tabs[currentIndex - 1]);
    }
});
