style:
/* Reset styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	transition: background 0.3s, color 0.3s;
}

body {
	font-family: Arial, sans-serif;
	background: var(--bg);
	color: var(--text);
	display: flex;
	align-items: center;
	justify-content: center;
	height: 100vh;
	padding: 20px;
}

/* Light & Dark Theme Variables */
:root {
	--bg: #f5f5f5;
	--text: #333;
	--card-bg: #fff;
	--border: #ddd;
	--primary: #3498db;
	--hover: rgba(0, 0, 0, 0.1);
}

.dark-mode {
	--bg: #1e1e1e;
	--text: #f5f5f5;
	--card-bg: #292929;
	--border: #444;
	--primary: #4db8ff;
	--hover: rgba(255, 255, 255, 0.1);
}

/* Theme Toggle Button */
.theme-toggle {
	position: absolute;
	top: 15px;
	right: 20px;
	background: var(--card-bg);
	border: 1px solid var(--border);
	padding: 8px 12px;
	border-radius: 20px;
	cursor: pointer;
	font-size: 16px;
	color: var(--text);
	display: flex;
	align-items: center;
	gap: 8px;
}

.theme-toggle:hover {
	background: var(--hover);
}

/* Card Container */
.card {
	background: var(--card-bg);
	padding: 30px;
	border-radius: 10px;
	box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);
	border: 1px solid var(--border);
	width: 400px;
	text-align: center;
}

/* Tab Buttons */
.tab-header {
	display: flex;
	justify-content: space-between;
	margin-bottom: 20px;
}

.tab-header button {
	flex: 1;
	background: none;
	border: none;
	color: var(--text);
	padding: 12px;
	font-size: 16px;
	cursor: pointer;
	transition: 0.3s;
	display: flex;
	flex-direction: column;
	align-items: center;
	gap: 5px;
}

.tab-header button i {
	font-size: 22px;
}

.tab-header button.active {
	color: var(--primary);
	font-weight: bold;
}

.tab-header button:hover {
	background: var(--hover);
	border-radius: 5px;
}

/* Tab Content */
.tab-content > div {
	display: none;
	animation: fadeIn 0.3s ease-in-out;
}

.tab-content > div.active {
	display: block;
}

@keyframes fadeIn {
	from {
		opacity: 0;
	}
	to {
		opacity: 1;
	}
}

/* Responsive */
@media (max-width: 480px) {
	.card {
		width: 100%;
	}

	.tab-header {
		flex-wrap: wrap;
	}

	.tab-header button {
		flex: 50%;
		padding: 10px;
		font-size: 14px;
	}

	.tab-header button i {
		font-size: 18px;
	}
}

script:
document.addEventListener("DOMContentLoaded", function () {
	const tabButtons = document.querySelectorAll(".tab-header button");
	const tabPanels = document.querySelectorAll(".tab-content > div");
	const themeToggle = document.querySelector(".theme-toggle");
	const body = document.body;

	// Handle Tab Switching
	tabButtons.forEach((button) => {
		button.addEventListener("click", function () {
			tabButtons.forEach((btn) => btn.classList.remove("active"));
			tabPanels.forEach((panel) => panel.classList.remove("active"));

			this.classList.add("active");
			const tabId = this.getAttribute("data-tab");
			document.getElementById(tabId).classList.add("active");
		});
	});

	// Theme Toggle Function
	themeToggle.addEventListener("click", function () {
		body.classList.toggle("dark-mode");

		if (body.classList.contains("dark-mode")) {
			themeToggle.innerHTML = '<i class="fa-solid fa-sun"></i> Light Mode';
		} else {
			themeToggle.innerHTML = '<i class="fa-solid fa-moon"></i> Dark Mode';
		}
	});
});
