style:
/* Reset and Base Styles */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Segoe UI", sans-serif;
}

body {
	background: #f0f2f5;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	padding: 2rem;
}

/* Tabs Container */
.tabs-container {
	background: white;
	width: 100%;
	max-width: 800px;
	border-radius: 12px;
	box-shadow: 0 8px 24px rgba(0, 0, 0, 0.1);
	overflow: hidden;
}

/* Tab Navigation */
.tab-nav {
	display: flex;
	flex-wrap: wrap;
	gap: 4px;
	background: #f8f9fa;
	padding: 1rem;
}

.tab-btn {
	flex: 1 1 180px;
	background: white;
	border: none;
	padding: 1rem 2rem;
	cursor: pointer;
	display: flex;
	align-items: center;
	gap: 0.75rem;
	font-size: 1rem;
	color: #495057;
	transition: all 0.3s ease;
	border-radius: 8px;
}

.tab-btn:hover {
	background: #e9ecef;
}

.tab-btn.active-tab {
	background: #228be6;
	color: white;
	box-shadow: 0 4px 12px rgba(34, 139, 230, 0.3);
}

/* Tab Content */
.tab-content {
	padding: 2rem;
	display: none;
	animation: fadeIn 0.4s ease;
}

.tab-content.active-content {
	display: block;
}

.tab-content h2 {
	color: #1c7ed6;
	margin-bottom: 1.5rem;
	font-size: 1.75rem;
}

.tab-content ul {
	list-style: none;
}

.tab-content p {
	line-height: 1.6;
	color: #495057;
	margin-bottom: 1rem;
}

/* Animations */
@keyframes fadeIn {
	from {
		opacity: 0;
		transform: translateY(10px);
	}
	to {
		opacity: 1;
		transform: translateY(0);
	}
}

/* Responsive Design */
@media (max-width: 768px) {
	.tab-nav {
		gap: 8px;
		padding: 0.5rem;
	}

	.tab-btn {
		padding: 0.75rem 1rem;
		font-size: 0.9rem;
	}

	.tab-content {
		padding: 1.5rem;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const tabButtons = document.querySelectorAll(".tab-btn");
	const tabContents = document.querySelectorAll(".tab-content");

	tabButtons.forEach((button) => {
		button.addEventListener("click", () => {
			// Remove active classes from all buttons and contents
			tabButtons.forEach((btn) => btn.classList.remove("active-tab"));
			tabContents.forEach((content) => content.classList.remove("active-content"));

			// Add active class to clicked button and corresponding content
			button.classList.add("active-tab");
			const tabId = button.getAttribute("data-tab");
			document.getElementById(tabId).classList.add("active-content");
		});
	});
});
