style:
:root {
    --primary: #3b82f6;
    --bg: #ffffff;
    --text: #1e293b;
    --card-bg: #f8fafc;
    --border: #e2e8f0;
}

.dark-mode {
    --primary: #60a5fa;
    --bg: #0f172a;
    --text: #f8fafc;
    --card-bg: #1e293b;
    --border: #334155;
}

* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    font-family: "Inter", system-ui, sans-serif;
    transition: 0.3s ease;
}

body {
    background: var(--bg);
    color: var(--text);
    min-height: 100vh;
    display: grid;
    place-items: center;
}

.theme-btn {
    position: fixed;
    top: 2rem;
    right: 2rem;
    background: var(--card-bg);
    border: 2px solid var(--border);
    padding: 0.75rem;
    border-radius: 50%;
    cursor: pointer;
    display: grid;
    place-items: center;
}

.container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem;
    overflow: hidden;
}

.testimonial-carousel {
    position: relative;
    padding: 2rem 0;
}

.carousel-inner {
    display: flex;
    transition: transform 0.5s ease-in-out;
}

.testimonial-card {
    min-width: 100%;
    padding: 2rem;
    background: var(--card-bg);
    border-radius: 1.5rem;
    border: 1px solid var(--border);
    margin: 0 1rem;
    box-shadow: 0 1rem 2rem rgba(0, 0, 0, 0.1);
}

.card-header {
    display: flex;
    align-items: center;
    gap: 1rem;
    margin-bottom: 1.5rem;
}

.author-img {
    width: 4rem;
    height: 4rem;
    border-radius: 50%;
    object-fit: cover;
    border: 2px solid var(--primary);
}

.author-info h3 {
    margin-bottom: 0.25rem;
}

.author-info p {
    color: var(--primary);
    font-size: 0.9rem;
}

.testimonial-text {
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.carousel-controls {
    display: flex;
    gap: 1rem;
    justify-content: center;
    margin-top: 2rem;
}

.carousel-btn {
    background: var(--card-bg);
    border: 2px solid var(--border);
    padding: 0.75rem 1.25rem;
    border-radius: 2rem;
    cursor: pointer;
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.carousel-dots {
    display: flex;
    gap: 0.5rem;
    justify-content: center;
    margin-top: 1.5rem;
}

.dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: var(--border);
    cursor: pointer;
    transition: 0.3s ease;
}

.dot.active {
    background: var(--primary);
    transform: scale(1.2);
}

@media (max-width: 768px) {
    .testimonial-card {
        margin: 0 0.5rem;
        padding: 1.5rem;
    }

    .author-img {
        width: 3rem;
        height: 3rem;
    }
}

script:
const testimonials = [
    {
        text: "This service revolutionized our workflow. The support team is incredibly responsive and knowledgeable.",
        author: "Michael Johnson",
        role: "CTO at TechStart",
        image: "https://picsum.photos/200?random=17"
    },
    {
        text: "We've seen a 300% increase in productivity since implementation. Truly game-changing!",
        author: "Sarah Wilson",
        role: "Product Manager",
        image: "https://picsum.photos/200?random=18"
    },
    {
        text: "The perfect balance of power and simplicity. Our team adopted it in just a few days.",
        author: "David Chen",
        role: "Startup Founder",
        image: "https://picsum.photos/200?random=19"
    },
    {
        text: "Exceptional value for money. The best investment we've made this year.",
        author: "Emma Davis",
        role: "E-commerce Lead",
        image: "https://picsum.photos/200?random=20"
    }
];

// Theme Toggle
const themeBtn = document.getElementById("themeBtn");
let isDark = localStorage.getItem("theme") === "dark";

function updateTheme() {
    document.body.classList.toggle("dark-mode", isDark);
    localStorage.setItem("theme", isDark ? "dark" : "light");
    themeBtn.textContent = isDark ? "ðŸŒž" : "ðŸŒ“";
}

themeBtn.addEventListener("click", () => {
    isDark = !isDark;
    updateTheme();
});

// Initialize theme
updateTheme();

// Carousel Logic
let currentIndex = 0;
const carouselInner = document.getElementById("carouselInner");
const carouselDots = document.getElementById("carouselDots");

function createTestimonialCards() {
    testimonials.forEach((testimonial, index) => {
        const card = document.createElement("div");
        card.className = "testimonial-card";
        card.innerHTML = `
                    <div class="card-header">
                        <img src="${testimonial.image}" alt="${testimonial.author}" class="author-img">
                        <div class="author-info">
                            <h3>${testimonial.author}</h3>
                            <p>${testimonial.role}</p>
                        </div>
                    </div>
                    <p class="testimonial-text">${testimonial.text}</p>
                `;
        carouselInner.appendChild(card);

        // Create dots
        const dot = document.createElement("div");
        dot.className = `dot ${index === 0 ? "active" : ""}`;
        dot.addEventListener("click", () => goToSlide(index));
        carouselDots.appendChild(dot);
    });
}

function updateCarousel() {
    carouselInner.style.transform = `translateX(-${currentIndex * 100}%)`;
    document.querySelectorAll(".dot").forEach((dot, index) => {
        dot.classList.toggle("active", index === currentIndex);
    });
}

function goToSlide(index) {
    currentIndex = (index + testimonials.length) % testimonials.length;
    updateCarousel();
}

// Auto-advance every 5 seconds
setInterval(() => {
    currentIndex = (currentIndex + 1) % testimonials.length;
    updateCarousel();
}, 5000);

// Initialize
createTestimonialCards();
updateCarousel();
