style:
.toggle-btn {
	background: rgb(19, 18, 18);
	border: none;
	border-radius: 5px;
	padding: 8px;
}

.hamburger {
	cursor: pointer;
	z-index: 1100;
}

.hamburger .bar {
	width: 25px;
	height: 3px;
	background: #fff;
	margin: 5px 0;
	transition: 0.3s;
}

.toggle-btn.active .bar:nth-child(1) {
	transform: translateY(8px) rotate(45deg);
}

.toggle-btn.active .bar:nth-child(2) {
	opacity: 0;
}

.toggle-btn.active .bar:nth-child(3) {
	transform: translateY(-8px) rotate(-45deg);
}
