style:
/* Reset & Base Styles */
* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
}

body {
	background: #f0f0f0;
	display: flex;
	align-items: center;
	justify-content: center;
	height: 100vh;
	font-family: Arial, sans-serif;
}

/* Pulsating Circle Toggle Styles */
.pulse-toggle {
	width: 80px;
	height: 80px;
	border-radius: 50%;
	background-color: #ccc;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	transition: background-color 0.3s ease, box-shadow 0.3s ease;
	font-size: 1em;
	color: #fff;
	user-select: none;
}

.pulse-toggle input {
	display: none;
}

/* Active State with Pulsing Animation */
.pulse-toggle.on {
	background-color: #2196f3;
	box-shadow: 0 0 15px 5px rgba(33, 150, 243, 0.6);
	animation: pulse 1.5s infinite;
}

@keyframes pulse {
	0% {
		box-shadow: 0 0 15px 5px rgba(33, 150, 243, 0.6);
	}
	50% {
		box-shadow: 0 0 25px 10px rgba(33, 150, 243, 0.8);
	}
	100% {
		box-shadow: 0 0 15px 5px rgba(33, 150, 243, 0.6);
	}
}

/* Responsive Adjustments */
@media (max-width: 480px) {
	.pulse-toggle {
		width: 60px;
		height: 60px;
		font-size: 0.8em;
	}
}

script:
const pulseToggleInput = document.getElementById("pulseToggle");
const pulseToggleLabel = document.querySelector(".pulse-toggle");
const toggleText = document.querySelector(".toggle-text");

pulseToggleInput.addEventListener("change", function () {
	if (this.checked) {
		pulseToggleLabel.classList.add("on");
		toggleText.textContent = "ON";
	} else {
		pulseToggleLabel.classList.remove("on");
		toggleText.textContent = "OFF";
	}
});
