style:
:root {
	--bg-color: #ffffff;
	--text-color: #2d3436;
	--primary-color: #2dd4bf;
	--tooltip-bg: #2d3436;
	--tooltip-text: #ffffff;
	--border-radius: 4px;
	--transition-speed: 0.2s;
	--shadow: 0 2px 8px rgba(45, 52, 54, 0.1);
}

.dark-theme {
	--bg-color: #0f172a;
	--text-color: #f8fafc;
	--primary-color: #2dd4bf;
	--tooltip-bg: #f8fafc;
	--tooltip-text: #0f172a;
}

* {
	box-sizing: border-box;
	margin: 0;
	padding: 0;
	transition: all var(--transition-speed) ease;
}

body {
	font-family: "Segoe UI", system-ui, sans-serif;
	background-color: var(--bg-color);
	color: var(--text-color);
	line-height: 1.5;
	padding: 2rem;
	min-height: 100vh;
}

.container {
	max-width: 800px;
	margin: 2rem auto;
	display: grid;
	gap: 1rem;
	grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
}

.tooltip-container {
	position: relative;
	display: flex;
	justify-content: center;
}

.demo-button {
	padding: 0.6rem 1.2rem;
	background: var(--primary-color);
	color: #ffffff;
	border: none;
	border-radius: var(--border-radius);
	cursor: pointer;
	font-size: 0.85rem;
	width: 100%;
	max-width: 120px;
	position: relative;
	overflow: hidden;
	font-weight: 500;
}

.demo-button:hover {
	background: #22c3ae;
	transform: scale(1.02);
}

.tooltip {
	position: absolute;
	background: var(--tooltip-bg);
	color: var(--tooltip-text);
	padding: 0.5rem 0.9rem;
	border-radius: 3px;
	font-size: 0.8rem;
	pointer-events: none;
	opacity: 0;
	transition: opacity var(--transition-speed);
	white-space: nowrap;
	z-index: 1000;
	box-shadow: var(--shadow);
	border: 1px solid rgba(0, 0, 0, 0.05);
}

.tooltip::after {
	content: "";
	position: absolute;
	border-width: 5px;
	border-style: solid;
}

.tooltip-container:hover .tooltip {
	opacity: 1;
}

/* Positioning */
.tooltip[data-position="top"] {
	bottom: calc(100% + 8px);
	left: 50%;
	transform: translateX(-50%);
}
.tooltip[data-position="top"]::after {
	top: 100%;
	left: 50%;
	border-color: var(--tooltip-bg) transparent transparent transparent;
	transform: translateX(-50%);
}

.tooltip[data-position="bottom"] {
	top: calc(100% + 8px);
	left: 50%;
	transform: translateX(-50%);
}
.tooltip[data-position="bottom"]::after {
	bottom: 100%;
	left: 50%;
	border-color: transparent transparent var(--tooltip-bg) transparent;
	transform: translateX(-50%);
}

.tooltip[data-position="left"] {
	right: calc(100% + 8px);
	top: 50%;
	transform: translateY(-50%);
}
.tooltip[data-position="left"]::after {
	left: 100%;
	top: 50%;
	border-color: transparent transparent transparent var(--tooltip-bg);
	transform: translateY(-50%);
}

.tooltip[data-position="right"] {
	left: calc(100% + 8px);
	top: 50%;
	transform: translateY(-50%);
}
.tooltip[data-position="right"]::after {
	right: 100%;
	top: 50%;
	border-color: transparent var(--tooltip-bg) transparent transparent;
	transform: translateY(-50%);
}

.theme-toggle {
	position: fixed;
	top: 1rem;
	right: 1rem;
	padding: 0.6rem;
	background: var(--primary-color);
	color: white;
	border: none;
	border-radius: 50%;
	cursor: pointer;
	width: 40px;
	height: 40px;
	display: flex;
	align-items: center;
	justify-content: center;
	box-shadow: var(--shadow);
}

.theme-toggle::before {
	content: "‚òÄÔ∏è";
	font-size: 1.1rem;
}

.dark-theme .theme-toggle::before {
	content: "üåô";
}

.direction-showcase {
	text-align: center;
	margin: 3rem 0;
}

h2 {
	margin-bottom: 2rem;
	color: var(--text-color);
	font-weight: 600;
	font-size: 1.4rem;
	letter-spacing: -0.5px;
}
