style:
:root {
	--success: #00c853;
	--info: #2196f3;
	--warning: #ff9100;
	--error: #d50000;
	--surface: #ffffff;
	--text-primary: #212121;
	--text-secondary: #757575;
}

body {
	font-family: "Roboto", sans-serif;
	background: #f5f5f5;
	margin: 2rem;
}

.notification-stack {
	position: fixed;
	bottom: 1rem;
	right: 1rem;
	display: flex;
	flex-direction: column-reverse;
	gap: 1rem;
	max-width: 400px;
	width: 90%;
	z-index: 1000;
}

.notification {
	background: var(--surface);
	border-radius: 8px;
	padding: 1rem;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
	display: grid;
	grid-template-columns: auto 1fr auto;
	gap: 1rem;
	animation: slideUp 0.3s ease-out;
	position: relative;
	overflow: hidden;
}

@keyframes slideUp {
	from {
		transform: translateY(100%);
		opacity: 0;
	}
	to {
		transform: translateY(0);
		opacity: 1;
	}
}

.notification-exit {
	animation: slideDown 0.3s ease-in forwards;
}

@keyframes slideDown {
	to {
		transform: translateY(150%);
		opacity: 0;
	}
}

.notification-icon {
	width: 40px;
	height: 40px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 1.2rem;
}

.notification-content {
	display: flex;
	flex-direction: column;
	gap: 0.25rem;
}

.notification-title {
	font-weight: 500;
	color: var(--text-primary);
}

.notification-message {
	color: var(--text-secondary);
	font-size: 0.9rem;
}

.notification-actions {
	display: flex;
	gap: 0.5rem;
	align-self: flex-start;
}

.notification-progress {
	position: absolute;
	bottom: 0;
	left: 0;
	width: 100%;
	height: 3px;
	background: rgba(0, 0, 0, 0.1);
}

.notification-progress::after {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	transform-origin: left;
	animation: progress 5s linear forwards;
}

@keyframes progress {
	from {
		transform: scaleX(1);
	}
	to {
		transform: scaleX(0);
	}
}

.btn {
	padding: 0.5rem 1rem;
	border: none;
	border-radius: 4px;
	cursor: pointer;
	font-weight: 500;
	transition: background 0.2s;
}

.btn--text {
	background: transparent;
	color: inherit;
}

.btn--text:hover {
	background: rgba(0, 0, 0, 0.05);
}

/* Notification Types */
.success {
	border-left: 4px solid var(--success);
}
.success .notification-icon {
	background: rgba(0, 200, 83, 0.1);
	color: var(--success);
}
.success .notification-progress::after {
	background: var(--success);
}

.info {
	border-left: 4px solid var(--info);
}
.info .notification-icon {
	background: rgba(33, 150, 243, 0.1);
	color: var(--info);
}
.info .notification-progress::after {
	background: var(--info);
}

.warning {
	border-left: 4px solid var(--warning);
}
.warning .notification-icon {
	background: rgba(255, 145, 0, 0.1);
	color: var(--warning);
}
.warning .notification-progress::after {
	background: var(--warning);
}

.error {
	border-left: 4px solid var(--error);
}
.error .notification-icon {
	background: rgba(213, 0, 0, 0.1);
	color: var(--error);
}
.error .notification-progress::after {
	background: var(--error);
}

.controls {
	display: flex;
	gap: 1rem;
	flex-wrap: wrap;
	margin-bottom: 2rem;
}

.control-btn {
	padding: 0.75rem 1.5rem;
	border-radius: 8px;
	color: white;
	border: none;
	cursor: pointer;
	transition: filter 0.2s;
}

.control-btn:hover {
	filter: brightness(1.1);
}

.success-btn {
	background: var(--success);
}
.info-btn {
	background: var(--info);
}
.warning-btn {
	background: var(--warning);
}
.error-btn {
	background: var(--error);
}

script:
const notificationData = {
	success: {
		icon: "fa-check",
		title: "Action Completed",
		message: "Your changes have been saved successfully",
		actions: ["Dismiss"],
	},
	info: {
		icon: "fa-info-circle",
		title: "New Feature",
		message: "Check out our latest updates",
		actions: ["View", "Dismiss"],
	},
	warning: {
		icon: "fa-exclamation-triangle",
		title: "Session Expiring",
		message: "Your session will expire in 5 minutes",
		actions: ["Extend", "Dismiss"],
	},
	error: {
		icon: "fa-times-circle",
		title: "Connection Lost",
		message: "Failed to connect to the server",
		actions: ["Retry", "Dismiss"],
	},
};

function showNotification(type) {
	const stack = document.getElementById("notificationStack");
	const data = notificationData[type];

	const notification = document.createElement("div");
	notification.className = `notification ${type}`;
	notification.innerHTML = `
		<div class="notification-icon">
			<i class="fas ${data.icon}"></i>
		</div>
		<div class="notification-content">
			<div class="notification-title">${data.title}</div>
			<div class="notification-message">${data.message}</div>
			<div class="notification-actions">
				${data.actions
					.map(
						(action) =>
							`<button class="btn btn--text" onclick="handleAction('${action}')">
							${action}
						</button>`,
					)
					.join("")}
			</div>
		</div>
		<button class="btn btn--text" onclick="dismissNotification(this)">
			<i class="fas fa-times"></i>
		</button>
		<div class="notification-progress"></div>
	`;

	stack.appendChild(notification);

	// Auto-dismiss after 5 seconds
	setTimeout(() => {
		notification.classList.add("notification-exit");
		setTimeout(() => notification.remove(), 300);
	}, 5000);
}

function dismissNotification(btn) {
	const notification = btn.closest(".notification");
	notification.classList.add("notification-exit");
	setTimeout(() => notification.remove(), 300);
}

function handleAction(action) {
	console.log(`Action: ${action}`);
	// Add custom action handling here
}
