style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	font-family: "Poppins", sans-serif;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	min-height: 100vh;
	background-color: #212529;
	padding: 2rem;
}

.alert-container {
	position: fixed;
	top: 2rem;
	right: 2rem;
	display: flex;
	flex-direction: column;
	gap: 1.5rem;
	z-index: 1000;
}

.alert {
	display: flex;
	align-items: center;
	padding: 1rem 1.5rem;
	border-radius: 1rem;
	width: 100%;
	max-width: 420px;
	animation: slideIn 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
	position: relative;
	overflow: hidden;
	transition: transform 0.2s, box-shadow 0.2s;
}

.alert:hover {
	transform: translateY(-2px);
	box-shadow: 0 8px 25px rgba(0, 0, 0, 0.15);
}

@keyframes slideIn {
	from {
		transform: translateX(100%);
		opacity: 0;
	}
	to {
		transform: translateX(0);
		opacity: 1;
	}
}

.alert.closing {
	animation: slideOut 0.3s ease-in forwards;
}

@keyframes slideOut {
	to {
		transform: translateX(150%);
		opacity: 0;
	}
}

.alert-content {
	display: flex;
	align-items: center;
	gap: 1rem;
	flex-grow: 1;
}

.alert-icon {
	width: 45px;
	height: 45px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	flex-shrink: 0;
}

.alert-message {
	color: #fff;
	font-size: 0.95rem;
	line-height: 1.4;
}

.alert-close {
	background: transparent;
	border: none;
	padding: 0.5rem;
	border-radius: 0.5rem;
	cursor: pointer;
	margin-left: 1rem;
	transition: all 0.2s ease;
}

.alert-close:hover {
	background: rgba(255, 255, 255, 0.1);
}

.progress-bar {
	position: absolute;
	bottom: 0;
	left: 0;
	height: 3px;
	width: 100%;
	background: rgba(255, 255, 255, 0.1);
}

.progress-bar::after {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	height: 100%;
	width: 100%;
	transform-origin: left;
	animation: progress 5s linear forwards;
}

@keyframes progress {
	from {
		transform: scaleX(1);
	}
	to {
		transform: scaleX(0);
	}
}

/* Alert Types */
.success {
	background: rgba(62, 189, 97, 0.2);
	border: 2px solid #3ebd61;
}
.success .alert-icon {
	background: #3ebd61;
}
.success .progress-bar::after {
	background: #3ebd61;
}

.info {
	background: rgba(0, 108, 227, 0.2);
	border: 2px solid #006ce3;
}
.info .alert-icon {
	background: #006ce3;
}
.info .progress-bar::after {
	background: #006ce3;
}

.warning {
	background: rgba(239, 148, 0, 0.2);
	border: 2px solid #ef9400;
}
.warning .alert-icon {
	background: #ef9400;
}
.warning .progress-bar::after {
	background: #ef9400;
}

.danger {
	background: rgba(236, 77, 43, 0.2);
	border: 2px solid #ec4d2b;
}
.danger .alert-icon {
	background: #ec4d2b;
}
.danger .progress-bar::after {
	background: #ec4d2b;
}

.controls {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 1rem;
	margin-top: 2rem;
}

.control-btn {
	padding: 1rem 2rem;
	border: none;
	border-radius: 0.75rem;
	cursor: pointer;
	font-weight: 500;
	transition: filter 0.2s;
	color: white;
}

.control-btn:hover {
	filter: brightness(1.1);
}

.success-btn {
	background: #3ebd61;
}
.info-btn {
	background: #006ce3;
}
.warning-btn {
	background: #ef9400;
}
.danger-btn {
	background: #ec4d2b;
}

script:
const alertData = {
	success: { icon: "fa-check", message: "Operation completed successfully" },
	info: { icon: "fa-info-circle", message: "System update available" },
	warning: { icon: "fa-exclamation-triangle", message: "Session expires in 5 minutes" },
	danger: { icon: "fa-times-circle", message: "Critical system failure detected" },
};

function showAlert(type) {
	const container = document.getElementById("alertContainer");
	const { icon, message } = alertData[type];

	const alert = document.createElement("div");
	alert.className = `alert ${type}`;
	alert.innerHTML = `
		<div class="alert-content">
			<div class="alert-icon">
				<i class="fas ${icon}"></i>
			</div>
			<p class="alert-message">${message}</p>
		</div>
		<button class="alert-close" onclick="closeAlert(this)">
			<i class="fas fa-times"></i>
		</button>
		<div class="progress-bar"></div>
	`;

	container.appendChild(alert);

	// Auto-remove after 5 seconds
	setTimeout(() => closeAlert(alert), 5000);
}

function closeAlert(element) {
	const alert = element.closest?.(".alert") || element;
	alert.classList.add("closing");
	setTimeout(() => alert.remove(), 300);
}
