style:
/* Global styles */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Roboto", sans-serif;
}

body {
  width: 100vw;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  background-color: #eeeeee;
  padding: 2rem;
}

.alert-container {
  position: fixed;
  top: 2rem;
  right: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  z-index: 1000;
}

.alert {
  width: 300px;
  background-color: #fff;
  padding: 1.2rem;
  box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
  border-radius: 10px;
  display: flex;
  align-items: center;
  gap: 1rem;
  animation: slideIn 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
  position: relative;
  overflow: hidden;
  transition: transform 0.2s;
}

.alert:hover {
  transform: translateY(-3px);
}

@keyframes slideIn {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

.alert::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  width: 5px;
  height: 100%;
}

.alert-content {
  display: flex;
  align-items: center;
  gap: 1rem;
  flex-grow: 1;
}

.alert-icon {
  width: 35px;
  height: 35px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
}

.alert-message {
  font-weight: 500;
  flex-grow: 1;
}

.alert-close {
  background: none;
  border: none;
  cursor: pointer;
  padding: 0.5rem;
  border-radius: 5px;
  transition: background 0.2s;
}

.alert-close:hover {
  background: rgba(0, 0, 0, 0.05);
}

.progress-bar {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 3px;
  background: rgba(0, 0, 0, 0.05);
}

.progress-bar::after {
  content: "";
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  transform-origin: left;
  animation: progress 5s linear forwards;
}

/* Alert Types */
.success::before {
  background: #4bb543;
}
.success .alert-icon {
  background: #4bb543;
}
.success .alert-message {
  color: #357b30;
}
.success .progress-bar::after {
  background: #4bb543;
}

.info::before {
  background: #646fd4;
}
.info .alert-icon {
  background: #646fd4;
}
.info .alert-message {
  color: #2a2f5c;
}
.info .progress-bar::after {
  background: #646fd4;
}

.warning::before {
  background: #ff8d29;
}
.warning .alert-icon {
  background: #ff8d29;
}
.warning .alert-message {
  color: #e47f26;
}
.warning .progress-bar::after {
  background: #ff8d29;
}

.error::before {
  background: #f32424;
}
.error .alert-icon {
  background: #f32424;
}
.error .alert-message {
  color: #f32424;
}
.error .progress-bar::after {
  background: #f32424;
}

.controls {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 1rem;
  margin-top: 2rem;
}

.control-btn {
  padding: 1rem 2rem;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 500;
  color: white;
  transition: filter 0.2s;
}

.control-btn:hover {
  filter: brightness(1.1);
}

.success-btn {
  background: #4bb543;
}
.info-btn {
  background: #646fd4;
}
.warning-btn {
  background: #ff8d29;
}
.error-btn {
  background: #f32424;
}

@keyframes progress {
  from {
    transform: scaleX(1);
  }
  to {
    transform: scaleX(0);
  }
}

script:
const alertConfig = {
  success: { icon: "fa-check", message: "Operation completed successfully" },
  info: { icon: "fa-info-circle", message: "System update available" },
  warning: { icon: "fa-exclamation-triangle", message: "Session expires in 5 minutes" },
  error: { icon: "fa-times-circle", message: "Critical system error detected" },
};

function showAlert(type) {
  const container = document.getElementById("alertContainer");
  const { icon, message } = alertConfig[type];

  const alert = document.createElement("div");
  alert.className = `alert ${type}`;
  alert.innerHTML = `
        <div class="alert-content">
            <div class="alert-icon">
                <i class="fas ${icon}"></i>
            </div>
            <p class="alert-message">${message}</p>
            <button class="alert-close" onclick="closeAlert(this)">
                <i class="fas fa-times"></i>
            </button>
        </div>
        <div class="progress-bar"></div>
    `;

  container.appendChild(alert);

  // Auto-remove after 5 seconds
  setTimeout(() => closeAlert(alert), 5000);
}

function closeAlert(element) {
  const alert = element.closest?.(".alert") || element;
  alert.style.transform = "translateX(150%)";
  alert.style.opacity = "0";
  setTimeout(() => alert.remove(), 300);
}
