style:
body {
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
	min-height: 100vh;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	background: #f8f9fa;
	margin: 0;
	padding: 20px;
}

.alert-stack {
	position: fixed;
	top: 20px;
	right: 20px;
	display: flex;
	flex-direction: column;
	gap: 15px;
	max-width: 400px;
	width: 100%;
}

.alert {
	position: relative;
	padding: 18px 25px;
	border-radius: 12px;
	color: white;
	display: flex;
	align-items: center;
	gap: 15px;
	transform: translateX(110%);
	animation: slideIn 0.4s forwards;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
}

@keyframes slideIn {
	to {
		transform: translateX(0);
	}
}

.alert.hide {
	animation: slideOut 0.4s forwards;
}

@keyframes slideOut {
	to {
		transform: translateX(150%);
	}
}

.alert-icon {
	font-size: 1.8em;
}

.close-btn {
	cursor: pointer;
	margin-left: auto;
	padding: 5px;
}

/* Alert Types */
.success {
	background: #4caf50;
}
.warning {
	background: #ff9800;
}
.error {
	background: #f44336;
}
.info {
	background: #2196f3;
}

.control-panel {
	display: flex;
	gap: 10px;
	margin-top: 20px;
}

.trigger-btn {
	padding: 12px 25px;
	border: none;
	border-radius: 8px;
	color: white;
	cursor: pointer;
}

script:
const alertConfig = {
	success: {
		icon: "fa-circle-check",
		message: "Operation completed successfully.",
	},
	warning: {
		icon: "fa-triangle-exclamation",
		message: "This action requires attention.",
	},
	error: {
		icon: "fa-bug",
		message: "Something went wrong. Try again.",
	},
	info: {
		icon: "fa-circle-info",
		message: "Here's some important information.",
	},
};

function createAlert(type) {
	const config = alertConfig[type];
	const alert = document.createElement("div");
	alert.className = `alert ${type}`;

	alert.innerHTML = `
                <i class="fa-solid ${config.icon} alert-icon"></i>
                <div>${config.message}</div>
                <span class="close-btn" onclick="removeAlert(this)">
                    <i class="fa-solid fa-xmark"></i>
                </span>
            `;

	document.querySelector(".alert-stack").appendChild(alert);

	setTimeout(() => {
		removeAlert(alert.querySelector(".close-btn"));
	}, 5000);
}

function removeAlert(closeBtn) {
	const alert = closeBtn.closest(".alert");
	alert.classList.add("hide");
	alert.addEventListener("animationend", () => alert.remove());
}
