style:
:root {
	--bg-color: #1a1a1a;
	--text-color: #ffffff;
	--card-bg: #2d2d2d;
	--success: #28a745;
	--warning: #ffc107;
	--error: #dc3545;
	--info: #17a2b8;
	--transition: all 0.3s ease;
}

body {
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
	background-color: var(--bg-color);
	color: var(--text-color);
	margin: 0;
	min-height: 100vh;
	transition: var(--transition);
}

.container {
	max-width: 800px;
	margin: 0 auto;
	padding: 20px;
}

.theme-toggle {
	position: fixed;
	top: 20px;
	right: 20px;
	background: var(--card-bg);
	border: none;
	padding: 10px;
	border-radius: 50%;
	cursor: pointer;
	transition: var(--transition);
}

.theme-toggle i {
	font-size: 1.5rem;
	color: var(--text-color);
}

.alert-container {
	position: fixed;
	top: 70px;
	right: 20px;
	max-width: 350px;
	width: 100%;
	z-index: 1000;
}

.alert {
	position: relative;
	padding: 15px 20px;
	margin: 10px 0;
	border-radius: 8px;
	display: flex;
	align-items: center;
	gap: 15px;
	background: var(--card-bg);
	transform: translateX(110%);
	animation: slideIn 0.4s forwards;
	box-shadow: 0 4px 6px rgba(0, 0, 0, 0.2);
}

@keyframes slideIn {
	to {
		transform: translateX(0);
	}
}

.alert.hide {
	animation: slideOut 0.4s forwards;
}

@keyframes slideOut {
	to {
		transform: translateX(150%);
	}
}

.alert i {
	font-size: 1.2rem;
}

.close-btn {
	margin-left: auto;
	cursor: pointer;
	opacity: 0.7;
	transition: var(--transition);
}

.close-btn:hover {
	opacity: 1;
}

.alert.success {
	border-left: 4px solid var(--success);
}
.alert.warning {
	border-left: 4px solid var(--warning);
}
.alert.error {
	border-left: 4px solid var(--error);
}
.alert.info {
	border-left: 4px solid var(--info);
}

.control-buttons {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 15px;
	margin-top: 100px;
}

.btn {
	padding: 12px 20px;
	border: none;
	border-radius: 6px;
	cursor: pointer;
	transition: var(--transition);
	font-weight: 500;
	display: flex;
	align-items: center;
	gap: 10px;
	justify-content: center;
}

.btn:hover {
	filter: brightness(1.1);
}

.btn-success {
	background: var(--success);
}
.btn-warning {
	background: var(--warning);
}
.btn-error {
	background: var(--error);
}
.btn-info {
	background: var(--info);
}

/* Light theme */
[data-theme="light"] {
	--bg-color: #f8f9fa;
	--text-color: #212529;
	--card-bg: #ffffff;
}

script:
// Theme toggle
function toggleTheme() {
	const body = document.body;
	const themeToggle = document.querySelector(".theme-toggle i");

	if (body.getAttribute("data-theme") === "light") {
		body.removeAttribute("data-theme");
		themeToggle.classList.replace("fa-sun", "fa-moon");
	} else {
		body.setAttribute("data-theme", "light");
		themeToggle.classList.replace("fa-moon", "fa-sun");
	}
}

// Alerts system
const alertMessages = {
	success: "Operation completed successfully!",
	warning: "Warning: This action requires attention!",
	error: "Error: Something went wrong!",
	info: "Information: Update available",
};

const alertIcons = {
	success: "fa-circle-check",
	warning: "fa-triangle-exclamation",
	error: "fa-circle-xmark",
	info: "fa-circle-info",
};

function showAlert(type) {
	const alertContainer = document.querySelector(".alert-container");
	const alert = document.createElement("div");

	alert.className = `alert ${type}`;
	alert.innerHTML = `
                <i class="fas ${alertIcons[type]}"></i>
                <span>${alertMessages[type]}</span>
                <span class="close-btn" onclick="closeAlert(this)">
                    <i class="fas fa-times"></i>
                </span>
            `;

	alertContainer.appendChild(alert);

	setTimeout(() => closeAlert(alert.querySelector(".close-btn")), 5000);
}

function closeAlert(element) {
	const alert = element.closest(".alert");
	alert.classList.add("hide");
	alert.addEventListener("animationend", () => alert.remove());
}
