style:
:root {
	--success: #10b981;
	--warning: #f59e0b;
	--error: #ef4444;
	--info: #3b82f6;
	--bg: #1e293b;
	--text: #f8fafc;
}

body {
	font-family: "Inter", sans-serif;
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: #0f172a;
	margin: 0;
	padding: 20px;
}

.notification-container {
	position: fixed;
	bottom: 20px;
	right: 20px;
	display: grid;
	gap: 12px;
	max-width: 400px;
	width: 100%;
}

.toast {
	position: relative;
	background: var(--bg);
	color: var(--text);
	padding: 16px;
	border-radius: 8px;
	display: grid;
	grid-template-columns: auto 1fr auto;
	align-items: center;
	gap: 12px;
	box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
	transform: translateX(150%);
	animation: slideIn 0.3s forwards;
	cursor: pointer;
	overflow: hidden;
}

@keyframes slideIn {
	to {
		transform: translateX(0);
	}
}

.toast.out {
	animation: slideOut 0.3s forwards;
}

@keyframes slideOut {
	to {
		transform: translateX(150%);
	}
}

.toast::after {
	content: "";
	position: absolute;
	bottom: 0;
	left: 0;
	height: 4px;
	background: currentColor;
	animation: progress 5s linear forwards;
}

@keyframes progress {
	from {
		width: 100%;
	}
	to {
		width: 0;
	}
}

.toast-icon {
	font-size: 1.5rem;
}

.toast-content {
	line-height: 1.4;
}

.toast-title {
	font-weight: 600;
	margin-bottom: 4px;
}

.toast-close {
	background: none;
	border: none;
	color: inherit;
	cursor: pointer;
	opacity: 0.7;
	transition: opacity 0.2s;
}

.toast-close:hover {
	opacity: 1;
}

.controls {
	display: flex;
	gap: 12px;
	margin-top: 20px;
}

.btn {
	padding: 12px 24px;
	border: none;
	border-radius: 6px;
	cursor: pointer;
	font-weight: 500;
	transition: transform 0.2s;
	color: white;
}

.btn:hover {
	transform: translateY(-2px);
}

.success {
	background: var(--success);
}
.warning {
	background: var(--warning);
}
.error {
	background: var(--error);
}
.info {
	background: var(--info);
}

script:
const toastData = {
	success: {
		icon: "fa-circle-check",
		title: "Success!",
		message: "Operation completed successfully.",
	},
	warning: {
		icon: "fa-triangle-exclamation",
		title: "Warning!",
		message: "Unexpected system behavior detected.",
	},
	error: {
		icon: "fa-circle-xmark",
		title: "Error!",
		message: "Failed to complete operation.",
	},
	info: {
		icon: "fa-circle-info",
		title: "Information",
		message: "New update available.",
	},
};

function createToast(type) {
	const container = document.querySelector(".notification-container");
	const toast = document.createElement("div");
	const data = toastData[type];

	toast.className = `toast ${type}`;
	toast.innerHTML = `
                <i class="fas ${data.icon} toast-icon"></i>
                <div class="toast-content">
                    <div class="toast-title">${data.title}</div>
                    <div class="toast-message">${data.message}</div>
                </div>
                <button class="toast-close" onclick="removeToast(this.parentElement)">
                    <i class="fas fa-times"></i>
                </button>
            `;

	// Swipe to dismiss
	let touchStartX = 0;

toast.addEventListener("touchstart", (e) => {
		touchStartX = e.changedTouches[0].screenX;
	});

toast.addEventListener("touchend", (e) => {
		const touchEndX = e.changedTouches[0].screenX;
		if (touchStartX - touchEndX > 50) removeToast(toast);
	});

	container.appendChild(toast);

	// Auto-remove after 5 seconds
	setTimeout(() => removeToast(toast), 5000);
}

function removeToast(toast) {
	toast.classList.add("out");
	toast.addEventListener("animationend", () => toast.remove());
}
