style:
/* Styling for smart notifications */
:root {
  --critical: #ff4757;
  --high: #ffa502;
  --normal: #2ed573;
  --low: #57606f;
  --bg: #2d3436;
  --text: #dfe4ea;
}

body {
  font-family: "Segoe UI", sans-serif;
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #1e272e;
  margin: 0;
  padding: 20px;
}

.notification-host {
  position: fixed;
  pointer-events: none;
  width: 100%;
  max-width: 320px;
}

.notification-host[data-position="top-right"] {
  top: 20px;
  right: 20px;
}

.notification-host[data-position="bottom-left"] {
  bottom: 20px;
  left: 20px;
}

.notification {
  background: var(--bg);
  color: var(--text);
  border-radius: 12px;
  margin: 10px 0;
  transform: scale(0.8);
  opacity: 0;
  animation: popIn 0.3s forwards;
  pointer-events: all;
  cursor: pointer;
  transition: transform 0.2s, box-shadow 0.2s;
  position: relative;
  overflow: hidden;
}

@keyframes popIn {
  to {
    transform: scale(1);
    opacity: 1;
  }
}

.notification.out {
  animation: popOut 0.3s forwards;
}

@keyframes popOut {
  to {
    transform: scale(0.8);
    opacity: 0;
  }
}

.notification-header {
  padding: 16px;
  display: flex;
  align-items: center;
  gap: 12px;
}

.notification-icon {
  font-size: 1.8em;
}

.notification-body {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s;
  padding: 0 16px;
}

.notification.expanded .notification-body {
  max-height: 200px;
  padding: 0 16px 16px;
}

.notification-progress {
  height: 4px;
  background: rgba(255, 255, 255, 0.1);
  position: relative;
}

.notification-progress::after {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;
  background: currentColor;
  animation: progress linear forwards;
}

@keyframes progress {
  from {
    width: 100%;
  }
  to {
    width: 0;
  }
}

.priority-critical {
  color: var(--critical);
}

.priority-high {
  color: var(--high);
}

.priority-normal {
  color: var(--normal);
}

.priority-low {
  color: var(--low);
}

.controls {
  position: fixed;
  bottom: 20px;
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: 10px;
}

.btn {
  padding: 12px 20px;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  font-weight: 500;
  background: var(--bg);
  color: var(--text);
  display: flex;
  align-items: center;
  gap: 8px;
}

.btn:hover {
  filter: brightness(1.2);
}

script:
const notificationConfig = {
  critical: {
    icon: "fa-solid fa-bug",
    title: "Critical Error",
    message: "System failure detected! Immediate action required.",
    duration: 10000,
    priority: "critical",
    position: "top-right",
  },
  high: {
    icon: "fa-solid fa-triangle-exclamation",
    title: "High Priority",
    message: "Security alert: Unauthorized access attempt detected.",
    duration: 7000,
    priority: "high",
    position: "top-right",
  },
  normal: {
    icon: "fa-solid fa-circle-check",
    title: "Operation Complete",
    message: "File upload successful. 245KB transferred.",
    duration: 4000,
    priority: "normal",
    position: "bottom-left",
  },
  low: {
    icon: "fa-solid fa-bell-slash",
    title: "Low Priority",
    message: "System maintenance scheduled for tomorrow 2AM.",
    duration: 4000,
    priority: "low",
    position: "bottom-left",
  },
};

function createNotification(type) {
  const config = notificationConfig[type];
  const host = document.querySelector(`.notification-host[data-position="${config.position}"]`);

  const notification = document.createElement("div");
  notification.className = `notification priority-${config.priority}`;
  notification.innerHTML = `
          <div class="notification-header">
              <i class="${config.icon} notification-icon"></i>
              <div>
                  <h3>${config.title}</h3>
              </div>
          </div>
          <div class="notification-body">
              <p>${config.message}</p>
          </div>
          <div class="notification-progress"></div>
      `;

  notification
    .querySelector(".notification-progress")
    .style.setProperty("--animation-duration", `${config.duration}ms`);

  notification.addEventListener("click", () => {
    notification.classList.toggle("expanded");
  });

  host.appendChild(notification);

  // Auto-remove
  setTimeout(() => {
    notification.classList.add("out");
    notification.addEventListener("animationend", () => notification.remove());
  }, config.duration);
}
