style:
/* Custom Styles */
:root {
  --bg: #ffffff;
  --text: #2d3748;
  --accent: #4f46e5;
  --surface: #f8fafc;
  --theme-bg: #1e293b;
}

.dark-mode {
  --bg: #0f172a;
  --text: #e2e8f0;
  --surface: #1e293b;
  --accent: #818cf8;
  --theme-bg: #f8fafc;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Inter", system-ui, sans-serif;
}

body {
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--bg);
  transition: background 0.4s ease;
}

.stepper {
  position: relative;
  padding: 2rem;
  background: var(--surface);
  border-radius: 24px;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
}

.breadcrumb-steps {
  display: flex;
  gap: 4rem;
  list-style: none;
  position: relative;
}

.step-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  z-index: 2;
}

.step-item:not(:last-child)::after {
  content: "";
  position: absolute;
  left: calc(100% + 1rem);
  top: 20px;
  width: 2rem;
  height: 2px;
  background: var(--text);
  opacity: 0.2;
}

.step-link {
  text-decoration: none;
  color: var(--text);
  display: flex;
  flex-direction: column;
  align-items: center;
  transition: all 0.3s ease;
}

.step-circle {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  background: var(--surface);
  border: 2px solid var(--text);
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 0.5rem;
  transition: all 0.3s ease;
}

.step-link:hover .step-circle {
  transform: scale(1.1);
  border-color: var(--accent);
  background: var(--accent);
  color: white;
}

.step-link.active .step-circle {
  background: var(--accent);
  border-color: var(--accent);
  color: white;
}

.theme-switch {
  position: fixed;
  top: 2rem;
  right: 2rem;
  width: 48px;
  height: 48px;
  border-radius: 50%;
  background: var(--surface);
  border: 2px solid var(--text);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.3s ease;
}

.theme-icon {
  width: 24px;
  height: 24px;
  transition: all 0.3s ease;
}

.sun {
  fill: var(--text);
}

.moon {
  fill: var(--text);
  opacity: 0;
  position: absolute;
}

.dark-mode .sun {
  opacity: 0;
}

.dark-mode .moon {
  opacity: 1;
}

@media (max-width: 768px) {
  .stepper {
    padding: 1.5rem;
    margin: 1rem;
  }

  .breadcrumb-steps {
    gap: 2rem;
  }

  .step-item:not(:last-child)::after {
    width: 1rem;
    left: calc(100% + 0.5rem);
  }
}

script:
document.addEventListener("DOMContentLoaded", () => {
  const body = document.body;
  const themeSwitch = document.querySelector(".theme-switch");

  // Theme toggle
  themeSwitch.addEventListener("click", () => {
    body.classList.toggle("dark-mode");
    localStorage.setItem("theme", body.classList.contains("dark-mode") ? "dark" : "light");
  });

  // Load saved theme
  if (localStorage.getItem("theme") === "dark") {
    body.classList.add("dark-mode");
  }

  // Step interactions
  document.querySelectorAll(".step-link").forEach((link) => {
    link.addEventListener("click", (e) => {
      e.preventDefault();
      document.querySelectorAll(".step-link").forEach((l) => l.classList.remove("active"));
      e.currentTarget.classList.add("active");
    });
  });
});
