style:
:root {
		--bg: #ffffff;
		--text: #2d3748;
		--accent: #4f46e5;
		--surface: #f8fafc;
		--divider: #e2e8f0;
		--theme-icon: #f59e0b;
	}

	.dark-theme {
		--bg: #1a202c;
		--text: #e2e8f0;
		--surface: #2d3748;
		--divider: #4a5568;
		--theme-icon: #ecc94b;
	}

	* {
		margin: 0;
		padding: 0;
		box-sizing: border-box;
		font-family: "Inter", system-ui, sans-serif;
	}

	body {
		min-height: 100vh;
		display: flex;
		align-items: center;
		justify-content: center;
		background: var(--bg);
		color: var(--text);
		transition: background 0.3s ease;
		padding: 2rem;
	}

	.theme-toggle {
		position: fixed;
		top: 2rem;
		right: 2rem;
		background: var(--surface);
		border: none;
		padding: 0.75rem;
		border-radius: 50%;
		cursor: pointer;
		box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
		transition: transform 0.2s ease;
	}

	.theme-toggle:hover {
		transform: scale(1.1);
	}

	.theme-icon {
		width: 24px;
		height: 24px;
		fill: var(--theme-icon);
		transition: opacity 0.3s ease;
	}

	.breadcrumb {
		background: var(--surface);
		padding: 1.5rem 2rem;
		border-radius: 16px;
		box-shadow: 0 10px 15px rgba(0, 0, 0, 0.05);
		max-width: 1200px;
		width: 100%;
	}

	.breadcrumb-list {
		display: flex;
		gap: 0.75rem;
		list-style: none;
		position: relative;
	}

	.breadcrumb-item {
		display: flex;
		align-items: center;
		position: relative;
	}

	.breadcrumb-item:not(:last-child)::after {
		content: "â€º";
		margin-left: 0.75rem;
		color: var(--text);
		opacity: 0.6;
		font-size: 1.25rem;
	}

	.breadcrumb-link {
		text-decoration: none;
		color: var(--text);
		padding: 0.5rem 1rem;
		border-radius: 8px;
		transition: all 0.2s ease;
		position: relative;
	}

	.breadcrumb-link::before {
		content: "";
		position: absolute;
		bottom: 0;
		left: 0;
		width: 0;
		height: 2px;
		background: var(--accent);
		transition: width 0.3s ease;
	}

	.breadcrumb-link:hover {
		color: var(--accent);
	}

	.breadcrumb-link:hover::before {
		width: 100%;
	}

	.breadcrumb-current {
		font-weight: 600;
		color: var(--accent);
		padding: 0.5rem 1rem;
	}

	@media (max-width: 768px) {
		body {
			padding: 1rem;
		}

		.breadcrumb {
			padding: 1rem;
		}

		.breadcrumb-link {
			padding: 0.5rem;
		}
	}

script:
document.addEventListener("DOMContentLoaded", () => {
	const themeToggle = document.querySelector(".theme-toggle");
	const body = document.body;
	const themeIcon = document.querySelector(".theme-icon");

	// Theme toggle
	themeToggle.addEventListener("click", () => {
		body.classList.toggle("dark-theme");
		const isDark = body.classList.contains("dark-theme");
		localStorage.setItem("theme", isDark ? "dark" : "light");

		// Animate icon
		themeIcon.style.opacity = "0";
		setTimeout(() => {
			themeIcon.style.opacity = "1";
		}, 150);
	});

	// Load saved theme
	if (localStorage.getItem("theme") === "dark") {
		body.classList.add("dark-theme");
	}

	// Breadcrumb interactions
	document.querySelectorAll(".breadcrumb-link").forEach((link) => {
		link.addEventListener("click", (e) => {
			e.preventDefault();
			console.log(`Navigating to: ${e.target.href}`);
		});
	});
});
