style:
:root {
	--bg: #f0f2f5;
	--text: #2d3748;
	--surface: #ffffff;
	--accent: #4f46e5;
	--theme-icon: #1f2937;
}

.dark-mode {
	--bg: #0f172a;
	--text: #f8fafc;
	--surface: #1e293b;
	--accent: #818cf8;
	--theme-icon: #fbbf24;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	background: var(--bg);
	transition: background 0.4s ease;
	padding: 2rem;
}

.breadcrumb-track {
	background: var(--surface);
	padding: 1.5rem 2rem;
	border-radius: 20px;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	position: relative;
	overflow: hidden;
}

.breadcrumb-list {
	display: flex;
	gap: 1.5rem;
	list-style: none;
	position: relative;
	z-index: 2;
}

.breadcrumb-item {
	display: flex;
	align-items: center;
	position: relative;
}

.breadcrumb-item:not(:last-child)::after {
	content: "âžž";
	margin-left: 1.5rem;
	color: var(--text);
	opacity: 0.4;
	animation: pulse 2s infinite;
}

.breadcrumb-link {
	text-decoration: none;
	color: var(--text);
	padding: 0.75rem 1.25rem;
	border-radius: 12px;
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.breadcrumb-link::before {
	content: "";
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: var(--accent);
	opacity: 0;
	transition: opacity 0.3s ease;
	z-index: -1;
}

.breadcrumb-link:hover {
	color: white;
	transform: translateY(-2px);
}

.breadcrumb-link:hover::before {
	opacity: 1;
}

.breadcrumb-current {
	font-weight: 600;
	color: var(--accent);
	padding: 0.75rem 1.25rem;
}

.theme-switch {
	position: fixed;
	top: 2rem;
	right: 2rem;
	width: 50px;
	height: 50px;
	background: var(--surface);
	border-radius: 15px;
	cursor: pointer;
	display: grid;
	place-items: center;
	box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
	transition: all 0.3s ease;
}

.theme-switch:hover {
	transform: rotate(15deg) scale(1.1);
}

.theme-icon {
	width: 24px;
	height: 24px;
	fill: var(--theme-icon);
	transition: fill 0.3s ease;
}

@keyframes pulse {
	0%,
	100% {
		opacity: 0.4;
	}
	50% {
		opacity: 0.8;
	}
}

@media (max-width: 768px) {
	body {
		padding: 1rem;
	}

	.breadcrumb-track {
		padding: 1rem;
		width: 95%;
	}

	.breadcrumb-list {
		flex-wrap: wrap;
		gap: 1rem;
	}

	.breadcrumb-item::after {
		margin-left: 0.5rem;
	}

	.theme-switch {
		top: 1rem;
		right: 1rem;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const body = document.body;
	const themeSwitch = document.querySelector(".theme-switch");

	// Theme toggle

themeSwitch.addEventListener("click", () => {
		body.classList.toggle("dark-mode");
		localStorage.setItem("theme", body.classList.contains("dark-mode") ? "dark" : "light");
	});

	// Load saved theme
	if (localStorage.getItem("theme") === "dark") {
		body.classList.add("dark-mode");
	}

	// Breadcrumb interactions

document.querySelectorAll(".breadcrumb-link").forEach((link) => {
		link.addEventListener("click", (e) => {
			e.preventDefault();
			console.log(`Navigating to: ${e.target.href}`);
		});
	});
});
