style:
:root {
	--bg: #ffffff;
	--text: #1f2937;
	--accent-1: #6366f1;
	--accent-2: #ec4899;
	--surface: #f8fafc;
}

.dark-mode {
	--bg: #0f172a;
	--text: #e2e8f0;
	--surface: #1e293b;
	--accent-1: #818cf8;
	--accent-2: #f472b6;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: "Inter", system-ui, sans-serif;
}

body {
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	background: var(--bg);
	transition: background 0.4s ease;
	padding: 2rem;
}

.breadcrumb {
	background: var(--surface);
	padding: 1.5rem 2rem;
	border-radius: 16px;
	box-shadow: 0 10px 15px rgba(0, 0, 0, 0.05);
	position: relative;
	max-width: 1200px;
	width: 100%;
}

.breadcrumb-list {
	display: flex;
	gap: 1rem;
	list-style: none;
	position: relative;
	flex-wrap: wrap;
	align-items: center;
	justify-content: flex-start;
}

.breadcrumb-item {
	position: relative;
	padding-right: 2rem;
}

.breadcrumb-item:not(:last-child)::after {
	content: "â€º";
	position: absolute;
	right: 0.5rem;
	color: var(--text);
	opacity: 0.6;
	font-size: 1.4rem;
	top: 50%;
	transform: translateY(-50%);
}

.breadcrumb-link {
	text-decoration: none;
	color: var(--text);
	padding: 0.5rem 1rem;
	border-radius: 8px;
	transition: all 0.3s ease;
	position: relative;
}

.breadcrumb-link::before {
	content: "";
	position: absolute;
	bottom: -2px;
	left: 0;
	width: 0;
	height: 2px;
	background: linear-gradient(45deg, var(--accent-1), var(--accent-2));
	transition: width 0.3s ease;
}

.breadcrumb-link:hover::before {
	width: 100%;
}

.breadcrumb-current {
	font-weight: 600;
	background: linear-gradient(45deg, var(--accent-1), var(--accent-2));
	-webkit-background-clip: text;
	background-clip: text;
	color: transparent;
}

.theme-switch {
	position: fixed;
	top: 2rem;
	right: 2rem;
	width: 60px;
	height: 32px;
	background: var(--surface);
	border-radius: 16px;
	cursor: pointer;
	border: 2px solid var(--text);
	transition: all 0.3s ease;
}

.theme-switch::before {
	content: "";
	position: absolute;
	width: 24px;
	height: 24px;
	background: var(--text);
	border-radius: 50%;
	left: 2px;
	top: 2px;
	transition: all 0.3s ease;
}

.dark-mode .theme-switch::before {
	transform: translateX(28px);
}

@media (max-width: 768px) {
	body {
		padding: 1rem;
	}

	.breadcrumb {
		padding: 1rem;
	}

	.breadcrumb-item {
		padding-right: 1.5rem;
	}

	.theme-switch {
		top: 1rem;
		right: 1rem;
	}
}

script:
document.addEventListener("DOMContentLoaded", () => {
	const body = document.body;
	const themeSwitch = document.querySelector(".theme-switch");

	// Theme toggle
	themeSwitch.addEventListener("click", () => {
		body.classList.toggle("dark-mode");
		localStorage.setItem("theme", body.classList.contains("dark-mode") ? "dark" : "light");
	});

	// Load saved theme
	if (localStorage.getItem("theme") === "dark") {
		body.classList.add("dark-mode");
	}

	// Breadcrumb interactions
	document.querySelectorAll(".breadcrumb-link").forEach((link) => {
		link.addEventListener("click", (e) => {
			e.preventDefault();
			console.log(`Navigating to: ${e.target.href}`);
		});
	});
});
