style:
:root {
	--bg: #ffffff;
	--text: #2d3436;
	--primary: #6c5ce7;
	--secondary: #a8a4ff;
	--radius: 1.5rem;
	--shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1);
}

[data-theme="dark"] {
	--bg: #2d3436;
	--text: #f5f6fa;
	--primary: #a8a4ff;
	--secondary: #6c5ce7;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	align-items: center;
	justify-content: center;
	background: var(--bg);
	color: var(--text);
	transition: all 0.4s ease;
	font-family: "Inter", system-ui;
}

.theme-btn {
	position: fixed;
	top: 2rem;
	right: 2rem;
	width: 3rem;
	height: 3rem;
	background: var(--primary);
	border: none;
	border-radius: 50%;
	cursor: pointer;
	display: grid;
	place-items: center;
	box-shadow: var(--shadow);
	transition: transform 0.3s ease;
}

.theme-btn:hover {
	transform: rotate(180deg) scale(1.1);
}

.breadcrumb {
	display: flex;
	gap: 0.5rem;
	padding: 1.5rem;
	background: var(--bg);
	border-radius: var(--radius);
	box-shadow: var(--shadow);
	position: relative;
	overflow: hidden;
}

.breadcrumb::before {
	content: "";
	position: absolute;
	inset: 0;
	background: linear-gradient(135deg, var(--primary) 0%, var(--secondary) 100%);
	opacity: 0.05;
	z-index: 0;
}

.crumb {
	position: relative;
	padding: 1rem 2rem;
	list-style: none;
	transition: all 0.3s ease;
}

.crumb a {
	text-decoration: none;
	color: var(--text);
	font-weight: 500;
	position: relative;
	z-index: 1;
	white-space: nowrap;
}

.crumb::after {
	content: "";
	position: absolute;
	right: -1rem;
	top: 50%;
	width: 1rem;
	height: 2px;
	background: var(--text);
	opacity: 0.2;
	transform: translateY(-50%);
}

.crumb:last-child::after {
	display: none;
}

.crumb:hover {
	transform: translateY(-3px);
}

.crumb:hover a {
	color: var(--primary);
}

.current {
	font-weight: 600;
	color: var(--primary);
}

@media (max-width: 640px) {
	.breadcrumb {
		flex-direction: column;
		padding: 2rem;
		width: 90%;
	}

	.crumb {
		padding: 1rem;
		width: 100%;
		text-align: center;
	}

	.crumb::after {
		right: 50%;
		top: auto;
		bottom: -1rem;
		width: 2px;
		height: 1rem;
		transform: translateX(50%);
	}
}

script:
const themeBtn = document.querySelector(".theme-btn");
const body = document.body;

themeBtn.addEventListener("click", () => {
	const isDark = body.getAttribute("data-theme") === "dark";
	body.setAttribute("data-theme", isDark ? "" : "dark");
	localStorage.setItem("theme", isDark ? "light" : "dark");
});

// Initialize theme
const savedTheme = localStorage.getItem("theme") || "light";
body.setAttribute("data-theme", savedTheme === "dark" ? "dark" : "");

// Breadcrumb animation
document.querySelectorAll(".crumb a").forEach((link) => {
	link.addEventListener("click", (e) => {
		e.preventDefault();
		link.closest(".crumb").animate(
			[
				{ transform: "scale(1)" },
				{ transform: "scale(0.95) rotate(-2deg)" },
				{ transform: "scale(1) rotate(0)" }
			],
			{
				duration: 400,
				easing: "ease-out"
			}
		);
	});
});
