style:
* {
margin: 0;
padding: 0;
box-sizing: border-box;
}

body {
min-height: 100vh;
display: flex;
justify-content: center;
align-items: center;
background: linear-gradient(45deg, #ff6b6b, #4ecdc4);
font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card-container {
max-width: 350px;
background: #fff;
border-radius: 15px;
overflow: hidden;
box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
transition: transform 0.3s ease;
}

.card-container:hover {
transform: translateY(-5px);
}

.card-image {
height: 200px;
position: relative;
overflow: hidden;
}

.card-image img {
width: 100%;
height: 100%;
object-fit: cover;
}

.card-content {
padding: 1.5rem;
}

.card-title {
font-size: 1.5rem;
color: #2d3436;
margin-bottom: 0.5rem;
}

.card-location {
display: flex;
align-items: center;
color: #636e72;
margin-bottom: 1rem;
}

.card-location i {
margin-right: 0.5rem;
color: #ff6b6b;
}

.amenities {
display: flex;
justify-content: space-between;
margin: 1rem 0;
padding: 1rem 0;
border-top: 1px solid #eee;
border-bottom: 1px solid #eee;
}

.amenity {
display: flex;
align-items: center;
font-size: 0.9rem;
}

.amenity i {
margin-right: 0.3rem;
color: #4ecdc4;
}

.price-book {
display: flex;
justify-content: space-between;
align-items: center;
margin-top: 1rem;
}

.price {
font-size: 1.8rem;
color: #2d3436;
font-weight: bold;
}

.price span {
font-size: 1rem;
color: #636e72;
font-weight: normal;
}

.book-btn {
background: #ff6b6b;
color: white;
padding: 0.8rem 1.5rem;
border: none;
border-radius: 25px;
cursor: pointer;
transition: background 0.3s ease;
}

.book-btn:hover {
background: #ff5252;
}
