style:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  min-height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
  background: linear-gradient(45deg, #6a11cb, #2575fc);
  font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card-container {
  max-width: 400px;
  background: #fff;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  transition: transform 0.3s ease;
}

.card-container:hover {
  transform: translateY(-5px);
}

.card-header {
  background: #2575fc;
  color: white;
  padding: 1.5rem;
  text-align: center;
}

.card-header h2 {
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
}

.card-header p {
  font-size: 0.9rem;
  opacity: 0.8;
}

.card-content {
  padding: 1.5rem;
}

.flight-details {
  display: flex;
  justify-content: space-between;
  margin-bottom: 1.5rem;
}

.flight-details .location {
  text-align: center;
}

.flight-details .location i {
  font-size: 1.5rem;
  color: #2575fc;
}

.flight-details .location h3 {
  font-size: 1.2rem;
  margin: 0.5rem 0;
}

.flight-details .location p {
  font-size: 0.9rem;
  color: #666;
}

.progress-bar {
  width: 100%;
  height: 10px;
  background: #e0e0e0;
  border-radius: 5px;
  overflow: hidden;
  margin: 1rem 0;
}

.progress-bar .progress {
  height: 100%;
  width: 60%;
  background: #2575fc;
  border-radius: 5px;
}

.progress-text {
  font-size: 0.9rem;
  color: #666;
  text-align: center;
  margin-bottom: 1.5rem;
}

.price-book {
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.price {
  font-size: 1.8rem;
  color: #2d3436;
  font-weight: bold;
}

.price span {
  font-size: 1rem;
  color: #666;
  font-weight: normal;
}

.book-btn {
  background: #2575fc;
  color: white;
  padding: 0.8rem 1.5rem;
  border: none;
  border-radius: 25px;
  cursor: pointer;
  transition: background 0.3s ease;
}

.book-btn:hover {
  background: #1a5bbf;
}
