style:
/* CSS Code */
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: #f0f2f5;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card-container {
	width: 350px;
	background: #fff;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s, box-shadow 0.3s;
	position: relative;
	cursor: pointer;
}

.card-container:hover {
	transform: translateY(-10px);
	box-shadow: 0 15px 40px rgba(0, 0, 0, 0.15);
}

.card-image {
	height: 250px;
	position: relative;
	overflow: hidden;
}

.card-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
	transition: transform 0.5s;
}

.card-container:hover .card-image img {
	transform: scale(1.05);
}

.gallery-thumbnails {
	position: absolute;
	bottom: 10px;
	left: 50%;
	transform: translateX(-50%);
	display: flex;
	gap: 8px;
}

.thumbnail {
	max-width: 80px;
	height: 40px;
	border-radius: 5px;
	border: 2px solid white;
	cursor: pointer;
	transition: transform 0.3s;
}

.thumbnail:hover {
	transform: scale(1.1);
}

.favorite-btn {
	position: absolute;
	top: 15px;
	right: 15px;
	background: rgba(255, 255, 255, 0.9);
	width: 40px;
	height: 40px;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	cursor: pointer;
	transition: all 0.3s;
}

.favorite-btn:hover {
	background: white;
	transform: scale(1.1);
}

.card-content {
	padding: 20px;
	position: relative;
}

.rating {
	position: absolute;
	top: -25px;
	background: #4caf50;
	color: white;
	padding: 5px 10px;
	border-radius: 20px;
	font-size: 0.9rem;
	display: flex;
	align-items: center;
}

.rating i {
	margin-right: 5px;
}

.card-title {
	font-size: 1.4rem;
	margin-bottom: 10px;
	color: #2d3436;
}

.price {
	font-size: 1.8rem;
	color: #2196f3;
	font-weight: bold;
	margin: 15px 0;
}

.price span {
	font-size: 1rem;
	color: #666;
	font-weight: normal;
}

.room-options {
	display: flex;
	gap: 10px;
	margin: 15px 0;
}

.room-btn {
	flex: 1;
	padding: 8px;
	border: 2px solid #eee;
	border-radius: 10px;
	cursor: pointer;
	transition: all 0.3s;
	text-align: center;
}

.room-btn.active {
	border-color: #2196f3;
	background: #e3f2fd;
}

.book-btn {
	width: 100%;
	padding: 15px;
	background: linear-gradient(45deg, #2196f3, #1976d2);
	color: white;
	border: none;
	border-radius: 12px;
	font-size: 1.1rem;
	cursor: pointer;
	transition: all 0.3s ease;
	position: relative;
	overflow: hidden;
}

.book-btn:hover {
	transform: translateY(-2px);
	box-shadow: 0 5px 15px rgba(33, 150, 243, 0.4);
	background: linear-gradient(45deg, #2a87d3, #224e7a);
}

@keyframes ripple {
	to {
		transform: scale(4);
		opacity: 0;
	}
}

.ripple {
	position: absolute;
	background: rgba(255, 255, 255, 0.5);
	transform: scale(0);
	animation: ripple 0.6s linear;
	border-radius: 50%;
}

script:
function changeImage(element) {
	document.getElementById("mainImage").src = element.src;
}

function toggleFavorite(btn) {
	const icon = btn.querySelector("i");
	icon.classList.toggle("far");
	icon.classList.toggle("fas");
	btn.style.background = icon.classList.contains("fas") ? "#ffebee" : "rgba(255, 255, 255, 0.9)";
}

function selectRoom(btn) {
	document.querySelectorAll(".room-btn").forEach((b) => b.classList.remove("active"));
	btn.classList.add("active");
	updatePrice(btn.innerText);
}

function updatePrice(roomType) {
	const prices = { Standard: 299, Deluxe: 399, Premium: 499 };
	document.querySelector(".price").innerHTML = `$${prices[roomType]}<span>/night</span>`;
}

function createRipple(e) {
	const btn = e.currentTarget;
	const ripple = document.createElement("div");
	ripple.className = "ripple";

	const rect = btn.getBoundingClientRect();
	ripple.style.left = `${e.clientX - rect.left}px`;
	ripple.style.top = `${e.clientY - rect.top}px`;

	btn.appendChild(ripple);
	setTimeout(() => ripple.remove(), 600);
}
