style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: #f0f2f5;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card {
	width: 350px;
	background: white;
	border-radius: 15px;
	overflow: hidden;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.08);
	transition: transform 0.3s;
	position: relative;
}

.card:hover {
	transform: translateY(-5px);
}

.card-badge {
	position: absolute;
	top: 15px;
	left: -5px;
	background: #ff4757;
	color: white;
	padding: 8px 20px;
	clip-path: polygon(0 0, 100% 0, 90% 100%, 0 100%);
	font-size: 0.9rem;
}

.card-image {
	height: 200px;
	position: relative;
}

.card-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.card-content {
	padding: 20px;
}

.card-header {
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin-bottom: 15px;
}

.duration {
	background: rgba(255, 71, 87, 0.1);
	color: #ff4757;
	padding: 5px 10px;
	border-radius: 20px;
	font-size: 0.9rem;
}

.card-title {
	font-size: 1.4rem;
	margin-bottom: 10px;
	color: #2d3436;
}

.highlights {
	display: flex;
	gap: 15px;
	margin: 15px 0;
	flex-wrap: wrap;
}

.highlight {
	display: flex;
	align-items: center;
	gap: 5px;
	color: #636e72;
}

.highlight i {
	color: #2ed573;
}

.price-section {
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin-top: 20px;
}

.price {
	font-size: 1.8rem;
	color: #2d3436;
	font-weight: bold;
}

.price s {
	font-size: 1rem;
	color: #636e72;
	margin-left: 5px;
}

.group-size {
	color: #636e72;
	font-size: 0.9rem;
}

.book-btn {
	width: 100%;
	padding: 15px;
	background: linear-gradient(45deg, #2ed573, #1dd1a1);
	color: white;
	border: none;
	border-radius: 10px;
	font-size: 1.1rem;
	cursor: pointer;
	transition: transform 0.2s;
	margin-top: 15px;
}

.book-btn:hover {
	transform: translateY(-2px);
}

.toggle-options {
	display: flex;
	background: #f8f9fa;
	border-radius: 10px;
	padding: 5px;
	margin: 15px 0;
}

.toggle-btn {
	flex: 1;
	padding: 10px;
	text-align: center;
	cursor: pointer;
	transition: all 0.3s;
	border-radius: 8px;
}

.toggle-btn.active {
	background: white;
	box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
}

script:
function togglePrice(btn, type) {
	// Remove active class from all buttons
	document.querySelectorAll(".toggle-btn").forEach((b) => b.classList.remove("active"));
	// Add active class to clicked button
	btn.classList.add("active");

	// Update price display
	const priceDisplay = document.getElementById("priceDisplay");
	if (type === "basic") {
		priceDisplay.innerHTML = "$299 <s>$399</s>";
	} else {
		priceDisplay.innerHTML = "$449 <s>$599</s>";
	}
}
