style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: linear-gradient(45deg, #6a11cb, #2575fc);
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card {
	width: 400px;
	background: white;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s;
}

.card:hover {
	transform: translateY(-5px);
}

.card-header {
	background: #2575fc;
	color: white;
	padding: 20px;
	text-align: center;
}

.card-header h2 {
	font-size: 1.5rem;
	margin-bottom: 10px;
}

.card-header p {
	font-size: 0.9rem;
	opacity: 0.8;
}

.card-content {
	padding: 20px;
}

.flight-details {
	display: flex;
	justify-content: space-between;
	margin-bottom: 20px;
}

.flight-details .location {
	text-align: center;
}

.flight-details .location i {
	font-size: 1.5rem;
	color: #2575fc;
}

.flight-details .location h3 {
	font-size: 1.2rem;
	margin: 10px 0;
}

.flight-details .location p {
	font-size: 0.9rem;
	color: #666;
}

.toggle-options {
	display: flex;
	background: #f8f9fa;
	border-radius: 10px;
	padding: 5px;
	margin: 20px 0;
}

.toggle-btn {
	flex: 1;
	padding: 10px;
	text-align: center;
	cursor: pointer;
	transition: all 0.3s;
	border-radius: 8px;
}

.toggle-btn.active {
	background: white;
	box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);
}

.price-section {
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin-top: 20px;
}

.price {
	font-size: 1.8rem;
	color: #2d3436;
	font-weight: bold;
}

.price span {
	font-size: 1rem;
	color: #666;
	font-weight: normal;
}

.book-btn {
	width: 100%;
	padding: 15px;
	background: linear-gradient(45deg, #2575fc, #1a5bbf);
	color: white;
	border: none;
	border-radius: 10px;
	font-size: 1.1rem;
	cursor: pointer;
	transition: transform 0.2s;
	margin-top: 20px;
}

.book-btn:hover {
	transform: translateY(-2px);
}

script:
function toggleFlightType(btn, type) {
	// Remove active class from all buttons
	document.querySelectorAll(".toggle-btn").forEach((b) => b.classList.remove("active"));
	// Add active class to clicked button
	btn.classList.add("active");

	// Update price display
	const priceDisplay = document.getElementById("priceDisplay");
	if (type === "one-way") {
		priceDisplay.innerHTML = "$199<span>/person</span>";
	} else {
		priceDisplay.innerHTML = "$349<span>/person</span>";
	}
}
