style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: linear-gradient(45deg, #6a11cb, #2575fc);
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card {
	width: 400px;
	background: white;
	border-radius: 20px;
	overflow: hidden;
	box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
	transition: transform 0.3s;
}

.card:hover {
	transform: translateY(-5px);
}

.card-header {
	background: #2575fc;
	color: white;
	padding: 20px;
	text-align: center;
}

.card-header h2 {
	font-size: 1.5rem;
	margin-bottom: 10px;
}

.card-header p {
	font-size: 0.9rem;
	opacity: 0.8;
}

.card-content {
	padding: 20px;
}

.car-details {
	display: flex;
	justify-content: space-between;
	margin-bottom: 20px;
}

.car-details .car-image {
	width: 150px;
	height: 100px;
	border-radius: 10px;
	overflow: hidden;
}

.car-details .car-image img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.car-details .car-info {
	flex: 1;
	margin-left: 20px;
}

.car-details .car-info h3 {
	font-size: 1.2rem;
	margin-bottom: 10px;
	color: #2d3436;
}

.car-details .car-info p {
	font-size: 0.9rem;
	color: #666;
}

.date-picker {
	display: flex;
	justify-content: space-between;
	margin: 20px 0;
}

.date-picker input {
	width: 48%;
	padding: 10px;
	border: 1px solid #ddd;
	border-radius: 10px;
	font-size: 0.9rem;
	outline: none;
}

.price-section {
	display: flex;
	justify-content: space-between;
	align-items: center;
	margin-top: 20px;
}

.price {
	font-size: 1.8rem;
	color: #2d3436;
	font-weight: bold;
}

.price span {
	font-size: 1rem;
	color: #666;
	font-weight: normal;
}

.book-btn {
	width: 100%;
	padding: 15px;
	background: linear-gradient(45deg, #2575fc, #1a5bbf);
	color: white;
	border: none;
	border-radius: 10px;
	font-size: 1.1rem;
	cursor: pointer;
	transition: transform 0.2s;
	margin-top: 20px;
}

.book-btn:hover {
	transform: translateY(-2px);
}

script:
const startDateInput = document.getElementById("startDate");
const endDateInput = document.getElementById("endDate");
const priceDisplay = document.getElementById("priceDisplay");

function calculatePrice() {
	const startDate = new Date(startDateInput.value);
	const endDate = new Date(endDateInput.value);
	const days = Math.ceil((endDate - startDate) / (1000 * 60 * 60 * 24));
	const pricePerDay = 49;
	const totalPrice = days * pricePerDay;

	if (days > 0) {
		priceDisplay.innerHTML = `$${totalPrice}<span>/total</span>`;
	} else {
		priceDisplay.innerHTML = `$${pricePerDay}<span>/day</span>`;
	}
}

startDateInput.addEventListener("change", calculatePrice);
endDateInput.addEventListener("change", calculatePrice);
