style:
 * {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	min-height: 100vh;
	display: flex;
	justify-content: center;
	align-items: center;
	background: #0a0e17;
	font-family: "Segoe UI", Tahoma, Geneva, Verdana, sans-serif;
}

.card {
	width: 800px;
	display: grid;
	grid-template-columns: 1fr 1.2fr;
	background: #161b25;
	border-radius: 20px;
	overflow: hidden;
	position: relative;
	box-shadow: 0 0 30px rgba(0, 212, 255, 0.1);
	border: 1px solid rgba(0, 212, 255, 0.1);
}

.media-section {
	position: relative;
	overflow: hidden;
	padding: 30px;
}

.car-image {
	width: 100%;
	height: 300px;
	object-fit: contain;
	filter: drop-shadow(0 0 20px rgba(0, 212, 255, 0.2));
	transition: transform 0.5s;
}

.card:hover .car-image {
	transform: translateX(10px);
}

.badge {
	position: absolute;
	top: 20px;
	left: 20px;
	background: #00d4ff;
	color: #0a0e17;
	padding: 8px 20px;
	border-radius: 30px;
	font-weight: bold;
	font-size: 0.9rem;
}

.details-section {
	padding: 30px;
	background: linear-gradient(45deg, #161b25, #1a2029);
	position: relative;
}

.specs-grid {
	display: grid;
	grid-template-columns: repeat(2, 1fr);
	gap: 20px;
	margin: 25px 0;
}

.spec-item {
	display: flex;
	align-items: center;
	padding: 15px;
	background: rgba(0, 212, 255, 0.05);
	border-radius: 10px;
	border: 1px solid rgba(0, 212, 255, 0.1);
}

.spec-item i {
	font-size: 1.5rem;
	margin-right: 15px;
	color: #00d4ff;
}

.price {
	font-size: 2.5rem;
	color: #00d4ff;
	margin: 20px 0;
	text-shadow: 0 0 15px rgba(0, 212, 255, 0.3);
}

.price small {
	font-size: 1rem;
	color: #6b7280;
}

.duration-slider {
	width: 100%;
	height: 5px;
	background: rgba(255, 255, 255, 0.1);
	border-radius: 5px;
	margin: 25px 0;
	position: relative;
}

.slider-thumb {
	width: 20px;
	height: 20px;
	background: #00d4ff;
	border-radius: 50%;
	position: absolute;
	top: -8px;
	left: 0;
	cursor: grab;
	box-shadow: 0 0 15px rgba(0, 212, 255, 0.3);
}

.book-btn {
	width: 100%;
	padding: 15px;
	background: #00d4ff;
	color: #0a0e17;
	border: none;
	border-radius: 10px;
	font-size: 1.1rem;
	font-weight: bold;
	cursor: pointer;
	transition: all 0.3s;
	position: relative;
	overflow: hidden;
}

.book-btn:hover {
	transform: translateY(-3px);
	box-shadow: 0 5px 25px rgba(0, 212, 255, 0.4);
}

.fuel-toggle {
	display: flex;
	gap: 10px;
	margin: 20px 0;
}

.fuel-btn {
	flex: 1;
	padding: 10px;
	background: rgba(0, 212, 255, 0.05);
	border: 1px solid rgba(0, 212, 255, 0.1);
	color: #6b7280;
	border-radius: 8px;
	cursor: pointer;
	transition: all 0.3s;
}

.fuel-btn.active {
	background: rgba(0, 212, 255, 0.2);
	color: #00d4ff;
	border-color: #00d4ff;
}

script:
function toggleFuel(btn) {
	document.querySelectorAll(".fuel-btn").forEach((b) => b.classList.remove("active"));
	btn.classList.add("active");
}

let isDragging = false;
const slider = document.querySelector(".duration-slider");
const thumb = document.querySelector(".slider-thumb");

function startDrag(e) {
	isDragging = true;
	document.addEventListener("mousemove", moveThumb);
	document.addEventListener("mouseup", stopDrag);
}

function moveThumb(e) {
	if (!isDragging) return;
	const rect = slider.getBoundingClientRect();
	let newX = e.clientX - rect.left;
	newX = Math.max(0, Math.min(newX, rect.width));
	thumb.style.left = newX + "px";
}

function stopDrag() {
	isDragging = false;
	document.removeEventListener("mousemove", moveThumb);
	document.removeEventListener("mouseup", stopDrag);
}
