style:
/* Global Styles */
body {
 font-family: Arial, sans-serif;
 background-color: #f5f5f5;
 margin: 0;
 padding: 20px;
 display: flex;
 justify-content: center;
 align-items: center;
 height: 100vh;
}

/* Card Container */
.booking-card {
 background-color: #fff;
 border-radius: 10px;
 box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
 max-width: 350px;
 overflow: hidden;
 display: flex;
 flex-direction: column;
 transition: transform 0.3s ease;
}

.booking-card:hover {
 transform: translateY(-5px);
}

/* Card Image */
.booking-card img {
 width: 100%;
 height: 200px;
 object-fit: cover;
}

/* Card Content */
.booking-card-content {
 padding: 20px;
 display: flex;
 flex-direction: column;
 gap: 10px;
}

.booking-card-title {
 font-size: 1.5rem;
 margin: 0;
 color: #333;
}

.booking-card-subtitle {
 font-size: 0.9rem;
 color: #777;
 margin: 0;
 display: flex;
 align-items: center;
 gap: 5px;
}

/* Rating */
.rating {
 color: #ffcc00;
 font-size: 0.9rem;
}

.rating span {
 color: #555;
 margin-left: 5px;
 font-size: 0.8rem;
}

/* Description */
.booking-card p {
 font-size: 0.9rem;
 color: #555;
 line-height: 1.4;
 margin: 10px 0;
}

/* Footer Section */
.details {
 display: flex;
 justify-content: space-between;
 align-items: center;
 margin-top: auto;
 padding-top: 10px;
 border-top: 1px solid #eee;
}

.price {
 font-size: 1.2rem;
 font-weight: bold;
 color: #333;
}

.book-now {
 background-color: #007bff;
 color: #fff;
 padding: 10px 20px;
 border: none;
 border-radius: 5px;
 cursor: pointer;
 font-size: 0.9rem;
 transition: background-color 0.3s ease;
}

.book-now:hover {
 background-color: #0056b3;
}
