style:
body {
  font-family: sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f4f4f4;
}

.card-container {
  display: flex;
  flex-wrap: wrap; /* Allow cards to wrap on smaller screens */
  justify-content: center;
  gap: 20px; /* Space between cards */
}

.card {
  width: 300px; /* Adjust as needed */
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  overflow: hidden; /* Hide overflowing image */
  transition: transform 0.2s; /* Smooth hover effect */
}

.card:hover {
  transform: translateY(-5px); /* Move card up slightly on hover */
}

.card-image {
  width: 100%;
  height: 200px; /* Adjust as needed */
  object-fit: cover; /* Maintain aspect ratio and cover container */
}

.card-content {
  padding: 20px;
}

.card-title {
  font-size: 1.2rem;
  font-weight: bold;
  margin-bottom: 10px;
}

.card-description {
  color: #666;
  margin-bottom: 15px;
  overflow: hidden; /* Hide overflowing text */
  text-overflow: ellipsis; /* Add ellipsis (...) for overflow */
  display: -webkit-box; /* Use -webkit-box for line clamping */
  -webkit-line-clamp: 3; /* Show maximum 3 lines */
  -webkit-box-orient: vertical;
}

.card-price {
  font-size: 1.1rem;
  font-weight: bold;
  color: #007bff; /* Example color */
  margin-bottom: 15px;
}

.card-booking {
  background-color: #007bff; /* Example color */
  color: #fff;
  padding: 10px 15px;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.2s; /* Smooth hover effect */
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 5px;
}

.card-booking:hover {
  background-color: #0056b3; /* Darker shade on hover */
}

.card-booking i {
  /* Icon style */
  margin-left: 5px;
}

/* Responsive Design - Adjust breakpoint as needed */
@media (max-width: 768px) {
  .card {
    width: 90%; /* Make cards take full width on smaller screens */
  }
}
