style:
body {
  font-family: sans-serif;
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
  background-color: #f0f0f0; /* Slightly different background */
}

.card-container {
  display: grid; /* Use grid for layout */
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); /* Responsive columns */
  gap: 20px;
}

.card {
  background-color: #fff;
  border-radius: 10px;
  box-shadow: 0 6px 10px rgba(0, 0, 0, 0.15); /* Softer shadow */
  overflow: hidden;
  transition: transform 0.3s ease-in-out; /* Smoother transition */
}

.card:hover {
  transform: scale(1.05); /* Scale up on hover */
}

.card-image {
  width: 100%;
  height: 250px; /* Increased image height */
  object-fit: cover;
}

.card-content {
  padding: 20px;
}

.card-title {
  font-size: 1.3rem;
  font-weight: 600; /* Slightly lighter font-weight */
  color: #333; /* Darker title color */
  margin-bottom: 10px;
}

.card-location {
  color: #777;
  font-size: 0.9rem;
  margin-bottom: 15px;
  display: flex;
  align-items: center; /* Vertically center icon and text */
  gap: 5px;
}

.card-location i {
  color: #007bff;
}

.card-description {
  color: #555;
  margin-bottom: 15px;
  overflow: hidden;
  text-overflow: ellipsis;
  display: -webkit-box;
  -webkit-line-clamp: 3;
  line-clamp: 3;
  -webkit-box-orient: vertical;
}

.card-price {
  font-size: 1.2rem;
  font-weight: bold;
  color: #28a745; /* Green price */
  margin-bottom: 15px;
}

.card-booking {
  background-color: #007bff;
  color: #fff;
  padding: 12px 20px; /* Larger button */
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: background-color 0.3s;
  display: inline-flex; /* Use inline-flex for better alignment */
  align-items: center;
  justify-content: center;
  gap: 8px; /* More space between icon and text */
}

.card-booking:hover {
  background-color: #0056b3;
}

.card-booking i {
  font-size: 1.1rem; /* Slightly larger icon */
}
