style:
* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
	font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
}

body {
	display: flex;
	justify-content: center;
	align-items: center;
	min-height: 100vh;
	background-color: #f0f2f5;
	padding: 20px;
}

.card {
	width: 800px;
	background: white;
	border-radius: 16px;
	display: flex;
	overflow: hidden;
	box-shadow: 0 10px 20px rgba(0, 0, 0, 0.08);
	transition: all 0.3s ease;
}

.card:hover {
	box-shadow: 0 15px 30px rgba(0, 0, 0, 0.15);
	transform: translateY(-2px);
}

.image-section {
	width: 40%;
	position: relative;
}

.image-section img {
	width: 100%;
	height: 100%;
	object-fit: cover;
}

.badge {
	position: absolute;
	top: 20px;
	left: 20px;
	background: rgba(0, 0, 0, 0.7);
	color: white;
	padding: 8px 16px;
	border-radius: 20px;
	font-size: 12px;
	font-weight: 500;
}

.content-section {
	width: 60%;
	padding: 32px;
	display: flex;
	flex-direction: column;
}

.header {
	display: flex;
	justify-content: space-between;
	align-items: flex-start;
	margin-bottom: 20px;
}

.title-section h2 {
	font-size: 24px;
	color: #1a1a1a;
	margin-bottom: 8px;
}

.location {
	display: flex;
	align-items: center;
	color: #666;
	font-size: 14px;
	margin-bottom: 4px;
}

.location i {
	margin-right: 6px;
	color: #4caf50;
}

.price-tag {
	background: #f8f9fa;
	padding: 12px 20px;
	border-radius: 12px;
	text-align: center;
}

.price {
	font-size: 26px;
	font-weight: 600;
	color: #1a1a1a;
}

.price-period {
	font-size: 14px;
	color: #666;
}

.amenities {
	display: flex;
	gap: 24px;
	margin: 20px 0;
}

.amenity {
	display: flex;
	align-items: center;
	gap: 8px;
	color: #666;
}

.amenity i {
	color: #4caf50;
}

.description {
	color: #666;
	font-size: 14px;
	line-height: 1.6;
	margin-bottom: 20px;
}

.footer {
	margin-top: auto;
	display: flex;
	justify-content: space-between;
	align-items: center;
}

.rating {
	display: flex;
	align-items: center;
	gap: 8px;
}

.stars {
	color: #ffd700;
}

.review-count {
	color: #666;
	font-size: 14px;
}

.book-button {
	background: #4caf50;
	color: white;
	border: none;
	padding: 12px 32px;
	border-radius: 8px;
	font-weight: 500;
	cursor: pointer;
	transition: background 0.3s ease;
}

.book-button:hover {
	background: #45a049;
}

@media screen and (max-width: 768px) {
	.card {
		flex-direction: column;
		width: 100%;
		max-width: 500px;
	}

	.image-section {
		width: 100%;
		height: 300px;
	}

	.content-section {
		width: 100%;
		padding: 24px;
	}

	.header {
		flex-direction: column;
		gap: 16px;
		margin-bottom: 16px;
	}

	.title-section h2 {
		font-size: 20px;
	}

	.price {
		font-size: 22px;
	}

	.amenities {
		gap: 16px;
		margin: 16px 0;
	}

	.description {
		font-size: 13px;
		margin-bottom: 16px;
	}

	.footer {
		flex-direction: column;
		gap: 16px;
		align-items: stretch;
	}

	.book-button {
		width: 100%;
		padding: 12px 24px;
	}
}

@media screen and (max-width: 480px) {
	.content-section {
		padding: 20px;
	}

	.title-section h2 {
		font-size: 18px;
	}

	.price {
		font-size: 20px;
	}

	.price-period {
		font-size: 12px;
	}

	.amenities {
		gap: 12px;
	}

	.amenity span {
		font-size: 12px;
	}

	.description {
		font-size: 12px;
	}

	.review-count {
		font-size: 12px;
	}

	.badge {
		padding: 6px 12px;
		font-size: 11px;
	}
}
